import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext'; // AuthProvider ve useAuth'u i�eri aktar�n
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import './index.css';

const express = require('express');
const cors = require('cors'); // cors paketini import edin
const app = express();

// CORS middleware'ini en �ste ekleyin
app.use(cors({
    origin: 'http://localhost:3000', // React uygulaman�z�n adresi
    credentials: true,
    methods: ['GET', 'POST', 'PUT', 'DELETE']
}));

// Di�er middleware'ler
app.use(express.json());

// Route'lar�n�z
app.post('/kayit', (req, res) => {
    // Kay�t i�lemleri
    res.json({ message: 'Kay�t ba�ar�l�' });
});

app.listen(3001, () => {
    console.log('Server 3001 portunda �al���yor');
});
function AppContent() {
    const { user } = useAuth(); // AuthContext'ten kullan�c� verisini al

    return (
        <Routes>
            {/* "/login" yolu i�in bile�en */}
            <Route
                path="/login"
                element={!user ? <LoginSignup apiBase="http://localhost:3000/login.html" /> : <Navigate to="/dashboard" replace />}
            />

            {/* "/dashboard" yolu i�in bile�en */}
            <Route
                path="/dashboard"
                element={user ? <Dashboard /> : <Navigate to="/login" replace />}
            />

            {/* Ana yol "/" i�in y�nlendirme */}
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
        </Routes>
    );
}

const ws = new WebSocket('ws://localhost:3000');

ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    setNotifications((prev) => [data, ...prev]);
};

// Uygulamay� saran ana bile�en (index.js'de �a�r�lacak)
export default function App() {
    return (
        <Router>
            <AuthProvider>
                <AppContent />
            </AuthProvider>
        </Router>
    );
}
