import React, { useState, useEffect } from "react";
import { useAuth } from "../AuthContext";
import "./ChunkDownloader.css";
import SecureViewer from './SecureViewer';

const BrainMemoryVisualization = ({ percentage, used, remaining }) => {
    return (
        <div className="brain-memory">
            <h3>ðŸ§  Yapay HafÄ±za</h3>
            <div className="brain-container">
                <div className="brain-fill-container">
                    <div
                        className="brain-fill"
                        style={{ height: `${percentage}%` }}
                    ></div>
                </div>
                <div className="brain-image"></div>
                <div className="brain-percentage">
                    {percentage.toFixed(1)}%
                </div>
            </div>
            <div className="memory-info">
                <div className="info-item">
                    <div className="info-value">{used.toFixed(1)} GB</div>
                    <div className="info-label">KullanÄ±lan</div>
                </div>
                <div className="info-item">
                    <div className="info-value">{remaining.toFixed(1)} GB</div>
                    <div className="info-label">Kalan</div>
                </div>
            </div>
        </div>
    );
};

const StoragePieChart = ({ percentage, used, remaining }) => {
    return (
        <div className="storage-pie">
            <h3>ðŸ’¾ Depolama AlanÄ±</h3>
            <div
                className="pie-chart"
                style={{ '--percentage': `${percentage}%` }}
            >
                <span className="percentage">{percentage.toFixed(1)}%</span>
            </div>
            <div className="memory-info">
                <div className="info-item">
                    <div className="info-value">{used} GB</div>
                    <div className="info-label">KullanÄ±lan</div>
                </div>
                <div className="info-item">
                    <div className="info-value">{remaining} GB</div>
                    <div className="info-label">Kalan</div>
                </div>
            </div>
        </div>
    );
};

const ChunkDownloader = ({ apiBase, userEmail }) => {
    const { user, logout } = useAuth();
    const [files, setFiles] = useState([]);
    const [canDownload, setCanDownload] = useState(true);
    const [uploading, setUploading] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);
    const [viewOption, setViewOption] = useState('standard');
    const [privacyOption, setPrivacyOption] = useState('private');
    const [totalStorage, setTotalStorage] = useState(0);
    const [viewDuration, setViewDuration] = useState('10s');
    const [sharingFile, setSharingFile] = useState(null);
    const [shareLinks, setShareLinks] = useState([]);
    const [memoryUsage, setMemoryUsage] = useState(0);
    const [viewingFile, setViewingFile] = useState(null); // Yeni state: gÃ¶rÃ¼ntÃ¼lenen dosya

    const viewDurationOptions = [
        { value: '10s', label: '10 Saniye', description: 'Medya 10 saniye gÃ¶rÃ¼ntÃ¼lendikten sonra kapanÄ±r' },
        { value: '30s', label: '30 Saniye', description: 'Medya 30 saniye gÃ¶rÃ¼ntÃ¼lendikten sonra kapanÄ±r' },
        { value: '1m', label: '1 Dakika', description: 'Medya 1 dakika gÃ¶rÃ¼ntÃ¼lendikten sonra kapanÄ±r' },
        { value: '5m', label: '5 Dakika', description: 'Medya 5 dakika gÃ¶rÃ¼ntÃ¼lendikten sonra kapanÄ±r' },
        { value: '1h', label: '1 Saat', description: 'Medya 1 saat gÃ¶rÃ¼ntÃ¼lendikten sonra kapanÄ±r' },
        { value: 'video', label: 'Video SÃ¼resi', description: 'Sadece video sÃ¼resi boyunca gÃ¶rÃ¼ntÃ¼lenir' },
        { value: 'unlimited', label: 'SÃ¼resiz', description: 'Manuel olarak kapatÄ±lana kadar aÃ§Ä±k kalÄ±r' },
    ];

    const getToken = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.log('âŒ Token bulunamadÄ±');
            return null;
        }
        return token;
    };

    const getBaseUrl = () => {
        return apiBase.replace('/api', '');
    };

    const getShareUrl = (file) => {
        // Ã–NEMLÄ°: file.view_token kullanÄ±yoruz!
        const baseUrl = getBaseUrl();
        return `${baseUrl}/share/${file.view_token}/`;
    };

    useEffect(() => {
        const interval = setInterval(() => {
            setMemoryUsage(Math.random() * 8 + 2);
        }, 5000);
        return () => clearInterval(interval);
    }, []);

    const fetchFiles = async () => {
        const token = getToken();
        if (!token) {
            logout();
            return;
        }

        try {
            console.log('ðŸ”„ Dosyalar Ã§ekiliyor...');
            const response = await fetch(`${apiBase}/files/`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                }
            });

            console.log('ðŸ“¡ Dosya response status:', response.status);

            if (response.ok) {
                const userFiles = await response.json();
