import React, { useState } from "react";
// Diğer bileşenler aynı klasörde
import Navbar from "./Navbar";
import Sidebar from "./Sidebar";
import ChunkDownloader from "./ChunkDownloader";
import GroupContent from "./GroupContent";
import AdPlayer from "./AdPlayer";
import ProfilePage from "./ProfilePage"; // Profili de ekledim

export default function Dashboard({ user, onLogout }) {
    const [active, setActive] = useState("downloader");
    const apiBase = "http://localhost:8000/api";

    const renderContent = () => {
        switch (active) {
            case "downloader":
                return <ChunkDownloader apiBase={apiBase} userEmail={user.email} />;
            case "groups":
                return <GroupContent apiBase={apiBase} userEmail={user.email} />;
            case "ads":
                return <AdPlayer apiBase={apiBase} userEmail={user.email} />;
            case "profile":
                return <ProfilePage user={user} />;
            default:
                return <p>Bir modül seçin</p>;
        }
    };

    return (
        <div className="h-screen flex flex-col">
            <Navbar user={user} onLogout={onLogout} />
            <div className="flex flex-1">
                <Sidebar active={active} setActive={setActive} />
                <div className="flex-1 p-6 bg-gray-50 overflow-y-auto">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}
