                setError("Sunucuya baÄŸlanÄ±rken hata oluÅŸtu.");
            }
        };

        if (token) fetchProfile();
    }, [token]);

    useEffect(() => {
        const loadProfile = async () => {
            const res = await apiFetch("http://127.0.0.1:8000/auth/profile/");
            if (res.ok) {
                const data = await res.json();
                setProfile(data);
            }
        };
        loadProfile();
    }, [apiFetch]);

