import React, { createContext, useState, useContext, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // sessionStorage'dan kullanıcı verisini oku ve başlangıç state'i olarak kullan
    // Eğer veri yoksa null olarak ayarla
    const [user, setUser] = useState(() => {
        try {
            const storedUser = sessionStorage.getItem("user");
            return storedUser ? JSON.parse(storedUser) : null;
        } catch (error) {
            console.error("Failed to parse user data from sessionStorage", error);
            return null;
        }
    });

    // useEffect kullanarak user state'i her değiştiğinde sessionStorage'ı güncelle
    useEffect(() => {
        if (user) {
            sessionStorage.setItem("user", JSON.stringify(user));
        } else {
            sessionStorage.removeItem("user");
        }
    }, [user]);

    const login = (userData) => setUser(userData);
    const logout = () => setUser(null);

    return (
        <AuthContext.Provider value={{ user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
export default ChunkDownloader;
