import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';

function App() {
    const { user, redirectTo, setRedirectTo } = useAuth();

    // Y�nlendirme i�in effect
    useEffect(() => {
        if (redirectTo) {
            window.location.href = redirectTo;
            setRedirectTo(null); // Y�nlendirme sonras� s�f�rla
        }
    }, [redirectTo, setRedirectTo]);

    <div className="login-container">
        <div className="login-form-box">
            {/* Giri� formunuzun i�eri�i buraya gelecek */}
        </div>
    </div>

    return (
        <Router>
            <div className="App">
                <Routes>
                    <Route path="/login" element={!user ? <LoginSignup /> : <Navigate to="/dashboard" replace />} />
                    <Route path="/dashboard" element={user ? <Dashboard /> : <Navigate to="/login" replace />} />
                    <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
