import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useApi } from "../utils/api";
import { Card, CardContent } from "./ui/Card";
import { Button } from "./ui/Button";
import { Input } from "./ui/Input";
import { Download } from "lucide-react";

export default function FileDetail() {
    const { fileId } = useParams();
    const { apiFetch } = useApi();
    const [file, setFile] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");

    const handleDownload = () => {
        window.open(`http://127.0.0.1:8001/groups/files/${fileId}/download/`, "_blank");
    };

    useEffect(() => {
        const loadFile = async () => {
            const res = await apiFetch(`http://127.0.0.1:8001/groups/files/${fileId}/`);
            if (res.ok) {
                const data = await res.json();
                setFile(data.file);
                setComments(data.comments);
            }
        };
        loadFile();
    }, [fileId]);

    const handleAddComment = async () => {
        const res = await apiFetch(`http://127.0.0.1:8001/groups/files/${fileId}/`, {
            method: "POST",
            body: JSON.stringify({ text: newComment }),
        });
        if (res.ok) {
            const data = await res.json();
            setComments((prev) => [data, ...prev]);
            setNewComment("");
        }
    };

    if (!file) return <p>Y√ºkleniyor...</p>;

    useEffect(() => {
        const handleKey = (e) => {
            if (e.key === "PrintScreen") {
                alert("‚ö†Ô∏è Ekran g√∂r√ºnt√ºs√º almak yasak!");
                e.preventDefault();
            }
        };

        const handleContext = (e) => {
            e.preventDefault();
            alert("‚ö†Ô∏è Saƒü tƒ±k devre dƒ±≈üƒ± bƒ±rakƒ±ldƒ±!");
        };

        window.addEventListener("keydown", handleKey);
        window.addEventListener("contextmenu", handleContext);

        return () => {
            window.removeEventListener("keydown", handleKey);
            window.removeEventListener("contextmenu", handleContext);
        };
    }, []);

    return (
        <div className="p-6">
            <Card className="mb-6">
                <CardContent>
                    <h2 className="text-xl font-bold">{file.filename}</h2>
                    <p className="text-gray-600">üì§ Y√ºkleyen: {file.uploaded_by}</p>
                    <p className="text-gray-500 text-sm">
                        üìÖ {new Date(file.created_at).toLocaleString()}
                    </p>

                    <div className="mt-4 flex gap-2">
                        <Button onClick={handleDownload} className="flex items-center gap-2">
                            <Download className="h-4 w-4" /> ƒ∞ndir
                        </Button>
                        {file.one_time_view && (
                            <span className="text-red-600 font-medium">
                                ‚ö†Ô∏è Bu dosya tek seferlik g√∂r√ºnt√ºlenebilir!
                            </span>
                        )}
                    </div>
                </CardContent>
            </Card>

            {/* Eƒüer tek seferlik ve view_url varsa inline g√∂ster */}
            {file.one_time_view && file.view_url && (
                <div className="relative border rounded overflow-hidden">
                    <iframe
                        src={file.view_url}
                        className="w-full h-96"
                        title="Secure Viewer"
                    ></iframe>

                    {/* Watermark */}
                    <div className="absolute inset-0 flex items-center justify-center opacity-30 pointer-events-none">
                        <span className="text-5xl font-bold text-gray-700 rotate-45">
                            CONFIDENTIAL
                        </span>
                    </div>
                </div>
            )}

            {/* Yorum ekleme + yorum listesi */}
            {/* ... aynƒ± kod ... */}
        </div>
    );
}
