import React, { useState, useEffect, useRef } from 'react';
import './ChatBox.css'; // Stil dosyanın burada olduğundan emin ol
import { Send, Cpu, FileText, Loader2, AlertCircle } from 'lucide-react'; // İkon seti (yoksa silebilirsin)

const ChatBox = () => {
    const [messages, setMessages] = useState([
        {
            id: 1,
            sender: 'ai',
            text: 'Merhaba Burak! Hafızamdaki projelere, kodlara veya anılarına dair ne sormak istersin?',
            timestamp: new Date().toISOString()
        }
    ]);
    const [inputText, setInputText] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef(null);

    // Otomatik kaydırma
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!inputText.trim()) return;

        const userMessage = {
            id: Date.now(),
            sender: 'user',
            text: inputText,
            timestamp: new Date().toISOString()
        };

        // 1. Kullanıcı mesajını ekle
        setMessages((prev) => [...prev, userMessage]);
        setInputText('');
        setIsLoading(true);

        try {
            // 2. Backend'e istek at (Token'ı localStorage'dan alıyoruz)
            const token = localStorage.getItem('access_token'); // Eğer auth yapın farklıysa burayı düzenle

            const response = await fetch('http://localhost:8000/memory/interact/', { // URL'i kendi portuna göre ayarla
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    message: userMessage.text,
                    context: {
                        current_page: window.location.pathname // Bağlam olarak şu anki sayfayı gönderiyoruz
                    }
                })
            });

            if (!response.ok) throw new Error('AI servisine ulaşılamadı.');

            const data = await response.json();

            // 3. AI Yanıtını İşle
            if (data.success) {
                const aiMessage = {
                    id: Date.now() + 1,
                    sender: 'ai',
                    text: data.data.reply,
                    memories: data.data.relevant_memories, // Backend'den gelen kaynak dosyalar
                    timestamp: new Date().toISOString()
                };
                setMessages((prev) => [...prev, aiMessage]);
            }

        } catch (error) {
            console.error('Chat Error:', error);
            const errorMessage = {
                id: Date.now() + 2,
                sender: 'system',
                text: 'Üzgünüm, şu an hafızama erişemiyorum. Lütfen bağlantını kontrol et.',
                isError: true
            };
            setMessages((prev) => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="chat-container">
            {/* Üst Başlık */}
            <div className="chat-header">
                <div className="flex items-center gap-2">
                    <Cpu className="w-5 h-5 text-blue-400" />
                    <h3 className="font-semibold text-white">Semantik Hafıza Asistanı</h3>
                </div>
                <span className="text-xs text-green-400 flex items-center gap-1">
                    <span className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></span>
                    Online
                </span>
            </div>

            {/* Mesaj Alanı */}
            <div className="messages-area">
                {messages.map((msg) => (
                    <div
                        key={msg.id}
                        className={`message-wrapper ${msg.sender === 'user' ? 'user' : 'ai'}`}
                    >
                        <div className={`message-bubble ${msg.isError ? 'error' : ''}`}>
                            <p>{msg.text}</p>

                            {/* Eğer AI hafızadan bir dosya bulduysa burada gösteriyoruz */}
                            {msg.memories && msg.memories.length > 0 && (
                                <div className="memory-sources">
                                    <span className="text-xs font-bold text-gray-300 mb-1 block">Kaynaklar:</span>
                                    {msg.memories.map((mem, idx) => (
                                        <div key={idx} className="memory-item">
                                            <FileText size={12} />
                                            <span>{mem.file_name}</span>
                                            <span className="opacity-50 text-[10px]">({mem.type})</span>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <span className="timestamp">
                                {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </span>
                        </div>
                    </div>
                ))}
                {isLoading && (
                    <div className="message-wrapper ai">
                        <div className="message-bubble loading">
                            <Loader2 className="animate-spin w-4 h-4" />
                            <span>Hafıza taranıyor...</span>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>

            {/* Giriş Alanı */}
            <form onSubmit={handleSendMessage} className="input-area">
                <input
                    type="text"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="Hafızamda bir şey ara..."
                    disabled={isLoading}
                />
                <button type="submit" disabled={isLoading || !inputText.trim()}>
                    <Send size={18} />
                </button>
            </form>
        </div>
    );
};

export default ChatBox;
