import React, { createContext, useState, useContext, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null); // user state'i eksikti
    const [redirectTo, setRedirectTo] = useState(null);

    const login = (userData) => {
        setUser(userData);
        setRedirectTo('/dashboard'); // Giriş başarılıysa dashboard'a yönlendir
    };

    const logout = () => {
        setUser(null);
        setRedirectTo('/login'); // Çıkış yapıldığında login sayfasına yönlendir
    };

    // Context değerini açıkça tanımlayın
    const value = {
        user,
        login,
        logout,
        redirectTo,
        setRedirectTo
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error("useAuth must be used within an AuthProvider");
    }
    return context;
};
