import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext'; 
import { MemoryProvider } from './components/MemoryContext'; 
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import './index.css';
import GlobalProtection from './components/GlobalProtection';


// Ana uygulama bileşeni
function App() {
    return (
        <AuthProvider>
            <GlobalProtection />
            <MemoryProvider>
                <AppContent />
            </MemoryProvider>
        </AuthProvider>
    );
}

// Uygulama içeriği bileşeni
function AppContent() {
    const { user, loading } = useAuth(); // loading'i buraya ekleyin
    const [notifications, setNotifications] = useState([]);

    // Token kontrolü
    useEffect(() => {
        const checkTokenExpiry = () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    const payload = JSON.parse(atob(token.split('.')[1]));
                    const expiry = payload.exp * 1000;
                    if (Date.now() >= expiry) {
                        localStorage.removeItem('token');
                        localStorage.removeItem('refresh');
                        window.location.href = '/login';
                    }
                } catch (error) {
                    console.error('Token decode hatası:', error);
                }
            }
        };

        const intervalId = setInterval(checkTokenExpiry, 60000);
        return () => clearInterval(intervalId);
    }, []);

    const handleKayit = async (kayitData) => {
        try {
            const response = await fetch('http://localhost:8001/api/auth/signup/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(kayitData)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            return data;

        } catch (error) {
            console.error('Signup error:', error);
            throw error;
        }
    };

    // Auth yükleniyorsa loading göster
    if (loading) {
        return (
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100vh',
                background: 'radial-gradient(circle at top left, rgba(59, 130, 246, 0.15) 5%, transparent 65%), radial-gradient(circle at bottom right, rgba(0, 206, 209, 0.1) 5%, transparent 65%), #050505'
            }}>
                <div style={{ color: 'white', textAlign: 'center' }}>
                    <div className="loading-spinner" style={{
                        width: '48px',
                        height: '48px',
                        border: '4px solid #e5e7eb',
                        borderTop: '4px solid #3b82f6',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite',
                        margin: '0 auto 20px'
                    }}></div>
                    <p>Yükleniyor...</p>
                </div>
            </div>
        );
    }

    return (
        <Routes>
            <Route
                path="/login"
                element={!user ? <LoginSignup onKayit={handleKayit} /> : <Navigate to="/dashboard" replace />}
            />
            <Route
                path="/dashboard"
                element={user ? <Dashboard notifications={notifications} /> : <Navigate to="/login" replace />}
            />
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
            <Route path="/login.html" element={<Navigate to="/login" replace />} />
        </Routes>
    );
}

export default App;
