import React, { useState, useEffect, useCallback, useRef } from "react";
import { useAuth } from "../AuthContext";
import { useMemory } from "./MemoryContext";
import ChunkDownloader from "./ChunkDownloader";
import GroupManager from './GroupManager';
import ChatBox from "./ChatBox";
import ChatHeader from "./ChatHeader";
import UserList from "./UserList";
import TrendingView from './TrendingView';
import "./styles/Dashboard.css";
import SearchEngine from './SearchEngine';

const MemoryStatsView = ({ stats }) => {
    if (!stats) return <div className="loading-state">HafÄ±za Ä°statistikleri yÃ¼kleniyor...</div>;

    const {
        total_items,
        memory_quota_mb,
        memory_used_mb,
        memory_remaining_mb,
        tier_distribution
    } = stats;

    // YÃ¼zde hesapla
    const usedPercentage = ((memory_used_mb / memory_quota_mb) * 100).toFixed(1);

    return (
        <div className="memory-stats-view">
            <h2>ðŸ§  Yapay HafÄ±za Durumu</h2>

            <div className="stat-card quota-card">
                <h3>KullanÄ±m ve Kota</h3>
                <div className="progress-bar-container">
                    <div
                        className="progress-bar"
                        style={{ width: `${usedPercentage}%` }}
                    >
                        {usedPercentage}%
                    </div>
                </div>
                <p>Toplam KullanÄ±lan: <strong>{memory_used_mb.toFixed(2)} MB</strong> / {memory_quota_mb.toFixed(2)} MB</p>
                <p>Kalan Kota: <strong>{memory_remaining_mb.toFixed(2)} MB</strong></p>
                <p>Toplam KayÄ±tlÄ± Ã–ÄŸe: <strong>{total_items}</strong></p>
            </div>

            <div className="stat-card tier-card">
                <h3>Katman DaÄŸÄ±lÄ±mÄ± (Memory Tier)</h3>
                <ul className="tier-list">
                    {tier_distribution.map((tier, index) => (
                        <li key={index}>
                            <span className={`tier-name tier-${tier.memory_tier__name.replace('_', '-')}`}>
                                {tier.memory_tier__name.toUpperCase()}
                            </span>
                            <span className="tier-count">{tier.count} Ã–ÄŸe</span>
                        </li>
                    ))}
                </ul>
            </div>

            {/* ... DiÄŸer detaylar eklenebilir (Ã–rn: En Ã§ok eriÅŸilen Ã¶ÄŸeler, Ã¶ÄŸrenme hÄ±zÄ±) ... */}
        </div>
    );
};

const WelcomeChatSection = ({ username, onFileSelect, onRefreshTimeline }) => { // onRefreshTimeline prop'u ekledik
    const [inputText, setInputText] = useState("");
    const [isProcessing, setIsProcessing] = useState(false);
    const [aiResponse, setAiResponse] = useState(null); // AI Metin CevabÄ±
    const [foundMemories, setFoundMemories] = useState([]); // Bulunan Dosyalar

    const handleKeyDown = async (e) => {
        if (e.key === 'Enter' && !isProcessing && inputText.trim()) {
            await handleSendMessage();
        }
    };

    const handleSendMessage = async () => {
        setIsProcessing(true);
        setAiResponse(null);
        setFoundMemories([]);

        try {
            const token = localStorage.getItem('token');
            // Backend endpoint'ine istek atÄ±yoruz
            const response = await fetch('http://localhost:8001/api/chat/ask/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ message: inputText })
            });

            const data = await response.json();

            if (response.ok) {
                setAiResponse(data.reply);
                // EÄŸer "Minecraft" veya "BalÄ±k" gibi bir ÅŸey bulunduysa buraya dÃ¼ÅŸer
                if (data.relevant_memories && data.relevant_memories.length > 0) {
                    setFoundMemories(data.relevant_memories);
                }

                // Kritik Nokta: Arama yapÄ±ldÄ±ÄŸÄ± iÃ§in kronolojiyi yenile
                if (onRefreshTimeline) onRefreshTimeline();
            }
        } catch (error) {
            console.error("AI HatasÄ±:", error);
            setAiResponse("BaÄŸlantÄ± hatasÄ± oluÅŸtu. LÃ¼tfen tekrar deneyin.");
        } finally {
            setIsProcessing(false);
            setInputText(""); // Inputu temizle
        }
    };

    return (
        <div className="welcome-chat-wrapper">
            <div className="welcome-header">
                <img src="/logo-tri.png" alt="Qyptos AI" className="welcome-logo-img" />
                <h1>Merhaba {username || 'KullanÄ±cÄ±'}</h1>
            </div>

            <div className="gemini-input-container">
                <button className="gemini-icon-btn file-btn">
                    <i className="fas fa-plus"></i>
                </button>

                <input
                    type="text"
                    placeholder="Qyptos AI'a sorun (Ã–rn: 'Minecraft videomu bul')..."
                    className="gemini-text-input"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    onKeyDown={handleKeyDown}
                    disabled={isProcessing}
                />

                <button
                    className={`gemini-icon-btn mic-btn ${isProcessing ? 'recording' : ''}`}
                    onClick={handleSendMessage} // Mikrofona ÅŸimdilik gÃ¶nder Ã¶zelliÄŸi verdik
                >
                    <i className={`fas ${isProcessing ? 'fa-spinner fa-spin' : 'fa-paper-plane'}`}></i>
                </button>
            </div>

            {/* AI YanÄ±tÄ± ve SonuÃ§ AlanÄ± */}
            {(aiResponse || foundMemories.length > 0) && (
                <div className="ai-result-area" style={{ maxWidth: '800px', margin: '20px auto', width: '100%' }}>
                    {aiResponse && (
                        <div className="ai-text-bubble" style={{ color: 'var(--text-primary)', marginBottom: '15px', lineHeight: '1.6' }}>
                            <i className="fas fa-robot" style={{ marginRight: '10px', color: 'var(--accent-blue)' }}></i>
                            {aiResponse}
                        </div>
                    )}

                    {/* Bulunan DosyalarÄ± Listele */}
                    {foundMemories.map((mem) => (
                        <MemoryResultCard key={mem.id} memory={mem} />
                    ))}
                </div>
            )}
        </div>
    );
};

const MemoryResultCard = ({ memory }) => {
    // memory objesi backend'den gelen "relevant_memories" iÃ§indeki bir Ã¶ÄŸedir.
    const isImage = memory.file_type === 'image' || memory.file_name.match(/\.(jpg|jpeg|png|gif)$/i);

    return (
        <div className="memory-result-card" style={{
            background: 'var(--bg-card)',
            border: '1px solid var(--accent-blue)',
            borderRadius: '12px',
            padding: '15px',
            marginTop: '15px',
            display: 'flex',
            gap: '15px',
            animation: 'fadeIn 0.5s ease'
        }}>
            {/* Varsa KÃ¼Ã§Ã¼k Resim, Yoksa Ä°kon */}
            <div className="memory-thumb" style={{ width: '80px', height: '80px', borderRadius: '8px', overflow: 'hidden', background: '#000' }}>
                {isImage && memory.thumbnail ? (
                    <img src={memory.thumbnail} alt={memory.file_name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                ) : (
                    <div style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '2rem' }}>
                        <i className={memory.file_type === 'video' ? "fas fa-video" : "fas fa-file-alt"}></i>
                    </div>
                )}
            </div>

            <div className="memory-info">
                <h4 style={{ margin: '0 0 5px 0', color: 'var(--accent-blue)' }}>{memory.file_name}</h4>
                <p style={{ margin: 0, fontSize: '0.9rem', color: 'var(--text-secondary)' }}>
                    {memory.summary || "Ä°Ã§erik analizi mevcut deÄŸil."}
                </p>
                <div style={{ marginTop: '10px', fontSize: '0.8rem', opacity: 0.7 }}>
                    <i className="fas fa-bullseye"></i> EÅŸleÅŸme Skoru: %{(memory.similarity_score * 100).toFixed(0)}
                </div>
            </div>
        </div>
    );
};

// --- YENÄ° BÄ°LEÅžEN: KRONOLOJÄ° Ã‡UBUÄžU ---
const ChronologyBar = () => {
    const [activeTab, setActiveTab] = useState('recent');

    const tabs = [
        { id: 'recent', label: 'En Son YapÄ±lan Aktiviteler' },
        { id: 'recurrent', label: 'TekrarladÄ±klarÄ±n' },
        { id: 'suggestions', label: 'Son Ä°ÅŸlemlere GÃ¶re Ã–neriler' }
    ];

    return (
        <div className="chronology-wrapper">
            <div className="chronology-track">
                {tabs.map(tab => (
                    <div
                        key={tab.id}
                        className={`chrono-item ${activeTab === tab.id ? 'active' : ''}`}
                        onClick={() => setActiveTab(tab.id)}
                    >
                        <div className="chrono-dot"></div>
                        <span>{tab.label}</span>
                    </div>
                ))}
                <div className="track-line"></div>
            </div>

            {/* Alt kÄ±sÄ±mda seÃ§ili sekmeye gÃ¶re ufak bir iÃ§erik gÃ¶sterimi (Opsiyonel) */}
            <div className="chrono-content-preview">
                {activeTab === 'recent' && <p>Son yÃ¼klenen dosya: <strong>System_Log_v2.csv</strong></p>}
                {activeTab === 'recurrent' && <p>SÄ±k kullanÄ±lan: <strong>HafÄ±za TemizliÄŸi</strong></p>}
                {activeTab === 'suggestions' && <p>Ã–neri: <strong>Veri sÄ±kÄ±ÅŸtÄ±rma iÅŸlemini baÅŸlat.</strong></p>}
            </div>
        </div>
    );
};

export default function Dashboard({ notifications = [] }) {
    const { user, logout } = useAuth();
    const { fetchMemoryStats: fetchMemoryStatsFromContext } = useMemory();
    const [memoryStats, setMemoryStats] = useState(null);
    const [storageStats, setStorageStats] = useState({ used: 0, total: 50 });
    const [activeTab, setActiveTab] = useState("downloader");
    const [notificationCount] = useState(notifications.length || 3);
    const [selectedThread, setSelectedThread] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [theme, setTheme] = useState('dark');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null); // DÄ±ÅŸarÄ± tÄ±klamayÄ± algÄ±lamak iÃ§in
    const [isThemeAnimating, setIsThemeAnimating] = useState(false);


    const fetchStorageData = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) return;

            // ChunkDownloader'Ä±n kullandÄ±ÄŸÄ± endpoint'i kullanÄ±yoruz
            const response = await fetch('http://localhost:8001/api/files/', {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (response.ok) {
                const files = await response.json();
                let totalBytes = 0;
                files.forEach(file => {
                    // EÄŸer API file_size dÃ¶nÃ¼yorsa onu kullan, yoksa tahmini hesapla
                    if (file.file_size) totalBytes += file.file_size;
                    else totalBytes += 1024 * 1024; // Fallback 1MB
                });

                // Byte -> GB DÃ¶nÃ¼ÅŸÃ¼mÃ¼
                const usedGB = totalBytes / (1024 * 1024 * 1024);
                setStorageStats({ used: usedGB, total: 50 }); // 50GB LÄ°MÄ°T
            }
        } catch (error) {
            console.error("Depolama verisi Ã§ekilemedi:", error);
        }
    };

    useEffect(() => {
        function handleClickOutside(event) {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setIsDropdownOpen(false);
            }
        }
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [dropdownRef]);

    // Memory Stats Fetch (AynÄ± MantÄ±k)
    const fetchMemoryStats = useCallback(async () => {
        try {
            if (fetchMemoryStatsFromContext) {
                const stats = await fetchMemoryStatsFromContext();
                setMemoryStats(stats);
            } else {
                // Mock data for visual consistency with image if API fails
                setMemoryStats({
                    memory_used_mb: 2.5,
                    memory_quota_mb: 100,
                    total_items: 42
                });
            }
        } catch (error) {
            console.error('Hata:', error);
            setMemoryStats({ memory_used_mb: 0, memory_quota_mb: 100 });
        }
    }, [fetchMemoryStatsFromContext]);

    useEffect(() => {
        fetchMemoryStats();
        fetchStorageData();

        // Periyodik gÃ¼ncelleme (Opsiyonel: her 30 saniyede bir gÃ¼ncelle)
        const interval = setInterval(() => {
            fetchStorageData();
            fetchMemoryStats();
        }, 30000);

        return () => clearInterval(interval);
    }, [fetchMemoryStats]);

    const handleLogout = () => {
        console.log('ðŸšª Dashboard logout butonuna tÄ±klandÄ±');
        logout();
    };

    const toggleTheme = () => {
        setIsThemeAnimating(true); // Animasyonu baÅŸlat

        // Animasyonun yarÄ±sÄ±nda temayÄ± deÄŸiÅŸtir (ikon dÃ¶nerken)
        setTimeout(() => {
            const newTheme = theme === 'dark' ? 'light' : 'dark';
            setTheme(newTheme);
            document.documentElement.setAttribute('data-theme', newTheme);
        }, 200);

        // Animasyon bitince sÄ±nÄ±fÄ± kaldÄ±r
        setTimeout(() => {
            setIsThemeAnimating(false);
        }, 500);
    };

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    const onChatFileSelect = (file) => {
        console.log("Chat Ã¼zerinden seÃ§ilen dosya:", file);
        alert(`"${file.name}" seÃ§ildi. YÃ¼kleme yÃ¶neticisine yÃ¶nlendiriliyorsunuz...`);
        setActiveTab("downloader");
        // Ä°sterseniz burada seÃ§ilen dosyayÄ± ChunkDownloader'a prop olarak geÃ§ebilirsiniz.
    };

    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);

        // Memory istatistiklerini yÃ¼kle
        fetchMemoryStats();

        const fetchOnlineUsers = async () => {
            try {
                const token = localStorage.getItem('token');
                console.log('ðŸ”‘ Dashboard - Token kontrolÃ¼:', token ? 'âœ… VAR' : 'âŒ YOK');

                if (!token) {
                    console.error('âŒ Token bulunamadÄ±! Login sayfasÄ±na yÃ¶nlendiriliyor...');
                    logout();
                    return;
                }

                console.log('ðŸ”„ KullanÄ±cÄ± listesi Ã§ekiliyor...');
                const response = await fetch('http://localhost:8001/chat/users/', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                console.log('ðŸ“¡ KullanÄ±cÄ± listesi response status:', response.status);

                if (response.status === 401) {
                    console.error('âŒ Token geÃ§ersiz veya sÃ¼resi dolmuÅŸ');
                    logout();
                    return;
                }

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const users = await response.json();
                console.log(`âœ… ${users.length} kullanÄ±cÄ± yÃ¼klendi`);
                setOnlineUsers(users);

            } catch (error) {
                console.error('âŒ KullanÄ±cÄ± listesi yÃ¼klenirken hata:', error);
                setOnlineUsers([]);
            }
        };

        fetchOnlineUsers();
    }, [logout, theme, fetchMemoryStats]); // fetchMemoryStats'Ä± dependency array'e ekle


    const renderContent = () => {
        switch (activeTab) {
            case "downloader":
                // Depolama YÃ¼zdesi Hesaplama
                const storagePercent = Math.min((storageStats.used / storageStats.total) * 100, 100).toFixed(1);

                // HafÄ±za YÃ¼zdesi Hesaplama
                const memoryPercent = memoryStats
                    ? ((memoryStats.memory_used_mb / memoryStats.memory_quota_mb) * 100).toFixed(1)
                    : 0;

                return (
                    <div className="dashboard-home-layout">

                        {/* 2. GEMINI SOHBET ALANI (Kronolojinin ÃœstÃ¼ne TaÅŸÄ±ndÄ±) */}
                        <WelcomeChatSection
                            username={user?.username}
                            onFileSelect={onChatFileSelect}
                        />

                        {/* 3. KRONOLOJÄ° Ã‡UBUÄžU */}
                        <ChronologyBar />

                        {/* 4. Ä°STATÄ°STÄ°KLER (GerÃ§ek ZamanlÄ±) */}
                        <div className="stats-grid-visual">
                            {/* Depolama KartÄ± */}
                            <div className="qyptos-card storage-card">
                                <div className="card-header">
                                    <i className="fas fa-archive"></i>
                                    <h3>Depolama AlanÄ±</h3>
                                </div>
                                <div className="circle-chart-wrapper">
                                    <svg viewBox="0 0 36 36" className="circular-chart blue">
                                        <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <path className="circle" strokeDasharray={`${storagePercent}, 100`} d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <text x="18" y="20.35" className="percentage">{storagePercent}%</text>
                                    </svg>
                                </div>
                                <div className="card-footer">
                                    <span>KullanÄ±lan: {storageStats.used.toFixed(2)} GB</span>
                                    <span>Toplam: {storageStats.total} GB</span>
                                </div>
                            </div>

                            {/* Yapay HafÄ±za KartÄ± */}
                            <div className="qyptos-card memory-card">
                                <div className="card-header">
                                    <i className="fas fa-brain"></i>
                                    <h3>Yapay HafÄ±za</h3>
                                </div>
                                <div className="circle-chart-wrapper">
                                    <svg viewBox="0 0 36 36" className="circular-chart purple">
                                        <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <path className="circle" strokeDasharray={`${memoryPercent}, 100`} d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <text x="18" y="20.35" className="percentage">{memoryPercent}%</text>
                                    </svg>
                                </div>
                                <div className="card-footer">
                                    <span>{memoryStats?.total_items || 0} Ã–ÄŸrenilen Bilgi</span>
                                </div>
                            </div>
                        </div>

                        {/* 5. DOSYA YÃœKLEME YÃ–NETÄ°CÄ°SÄ° */}
                        <div className="downloader-container-wrapper">
                            <ChunkDownloader apiBase="http://localhost:8001/api" />
                        </div>
                    </div>
                );
            case "groups":
                return <GroupManager />;

            case "memory": 
                return <MemoryStatsView stats={memoryStats} />;

            case "chat":
                return (
                    <div className="chat-container">
                        <div className="chat-sidebar">
                            <UserList
                                users={onlineUsers}
                                currentUser={user?.username}
                                onSelectUser={(username) => {
                                    const selectedUser = onlineUsers.find(u => u.username === username);
                                    setSelectedThread({
                                        id: Date.now(),
                                        username: username,
                                        isOnline: selectedUser?.is_online || false
                                    });
                                }}
                            />
                        </div>
                        <div className="chat-main">
                            {selectedThread ? (
                                <>
                                    <ChatHeader
                                        username={selectedThread.username}
                                        isOnline={selectedThread.isOnline}
                                    />
                                    <ChatBox
                                        threadId={selectedThread.id}
                                        currentUser={user?.username}
                                    />
                                </>
                            ) : (
                                <div className="no-chat-selected">
                                    <div className="no-chat-icon">ðŸ’¬</div>
                                    <h3>Bir sohbet baÅŸlatÄ±n</h3>
                                    <p>Sohbet etmek iÃ§in soldaki listeden bir kullanÄ±cÄ± seÃ§in</p>
                                </div>
                            )}
                        </div>
                    </div>
                );
            case "ads":
                return <TrendingView />;

            case "media":
                return <SearchEngine />;

            case "profile":
                return (
                    <div className="content-placeholder">
                        <h2>Profil AyarlarÄ±</h2>
                        <p>KullanÄ±cÄ±: {user?.username}</p>
                        <p>Email: {user?.email}</p>
                    </div>
                );
            default:
                return <div className="content-placeholder">Bir iÃ§erik seÃ§in</div>;
        }
    };

    return (
        <div className="dashboard-horizontal">
            {/* Ãœst Navigasyon Bar */}
            <nav className="top-nav">
                <div className="nav-left">
                    <div className="logo">
                        {/* Logo Yeri */}
                        <img
                            src="/qyptos-logo.png"
                            alt="qyptos-logo"
                            className="qyptos-logo-img"
                        />
                    </div>

                    <div className="nav-menu">
                        {/* Nav Itemlar aynÄ±... */}
                        <div className={`nav-item ${activeTab === "downloader" ? "active" : ""}`} onClick={() => setActiveTab("downloader")}>
                            <i className="fas fa-cloud"></i> <span>Bulutum</span>
                        </div>
                        <div className={`nav-item ${activeTab === "ads" ? "active" : ""}`} onClick={() => setActiveTab("ads")}>
                            <i className="fas fa-fire"></i> <span>Trendler</span>
                        </div>
                        <div className={`nav-item ${activeTab === "media" ? "active" : ""}`} onClick={() => setActiveTab("media")}>
                            <i className="fas fa-search"></i> <span>Ara</span>
                        </div>
                        <div className={`nav-item ${activeTab === "groups" ? "active" : ""}`} onClick={() => setActiveTab("groups")}>
                            <i className="fas fa-users"></i> <span>Gruplar</span>
                        </div>
                        <div className={`nav-item ${activeTab === "chat" ? "active" : ""}`} onClick={() => setActiveTab("chat")}>
                            <i className="fas fa-comments"></i> <span>DM</span>
                        </div>
                    </div>
                </div>

                <div className="nav-right">
                    <div className={`theme-toggle ${isThemeAnimating ? 'animating' : ''}`} onClick={toggleTheme}>
                        <i className={`fas ${theme === 'dark' ? 'fa-moon' : 'fa-sun'}`}></i>
                    </div>

                    <div className="notification-bell">
                        <i className="fas fa-bell"></i>
                        {notificationCount > 0 && (
                            <span className="notification-badge">{notificationCount}</span>
                        )}
                    </div>

                    {/* GÃœNCELLENEN KISIM: DROPDOWN MANTIÄžI */}
                    <div className="user-dropdown" onClick={toggleDropdown} ref={dropdownRef}>
                        <div className="user-avatar">
                            <i className="fas fa-user"></i>
                        </div>
                        <span className="user-name">{user?.username || 'burak'}</span>
                        <i className={`fas fa-chevron-down ${isDropdownOpen ? 'rotate' : ''}`}></i>

                        {/* Dropdown Menu Sadece isDropdownOpen true ise render edilir */}
                        {isDropdownOpen && (
                            <div className="dropdown-menu show">
                                <div className="dropdown-item" onClick={(e) => { e.stopPropagation(); setActiveTab("profile"); setIsDropdownOpen(false); }}>
                                    <i className="fas fa-user"></i>
                                    <span>Profil</span>
                                </div>
                                <div className="dropdown-item" onClick={(e) => { e.stopPropagation(); setIsDropdownOpen(false); }}>
                                    <i className="fas fa-cog"></i>
                                    <span>Ayarlar</span>
                                </div>
                                <div className="dropdown-divider"></div>
                                <div className="dropdown-item danger" onClick={(e) => { e.stopPropagation(); handleLogout(); }}>
                                    <i className="fas fa-sign-out-alt"></i>
                                    <span>Ã‡Ä±kÄ±ÅŸ Yap</span>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </nav>

            <main className="main-content-horizontal">
                {renderContent()}
            </main>
        </div>
    );
}
