// GroupManager.jsx - TUTARLI VERSÄ°YON
import React, { useState, useEffect } from 'react';
import { groupAPI, authAPI } from '../utils/api';
import './GroupManager.css';

const GroupManager = ({ onGroupSelect }) => {
    const [groups, setGroups] = useState([]);
    const [newGroupName, setNewGroupName] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isDemoMode, setIsDemoMode] = useState(true);
    const [showInviteModal, setShowInviteModal] = useState(false);
    const [selectedGroup, setSelectedGroup] = useState(null);
    const [inviteEmails, setInviteEmails] = useState('');
    const [inviteCode, setInviteCode] = useState('');

    useEffect(() => {
        console.log('ðŸŽ¯ GroupManager mounted - checking authentication');
        checkAuthentication();
    }, []);

    const checkAuthentication = async () => {
        try {
            setLoading(true);

            // Dashboard ile aynÄ± token kontrolÃ¼
            const token = localStorage.getItem('token');
            console.log('ðŸ” GroupManager Token kontrolÃ¼:', token ? 'âœ… VAR' : 'âŒ YOK');

            if (!token) {
                console.log('ðŸ”¶ Token yok, demo moda geÃ§iliyor...');
                handleDemoMode();
                return;
            }

            // Token varsa backend baÄŸlantÄ±sÄ±nÄ± test et
            await testBackendConnection();

        } catch (error) {
            console.error('âŒ Authentication check failed:', error);
            handleDemoMode();
        } finally {
            setLoading(false);
        }
    };

    const handleDemoMode = () => {
        console.log('ðŸ”¶ Demo mod etkin');
        const demoUser = {
            id: 1,
            username: 'Demo KullanÄ±cÄ±',
            email: 'demo@example.com'
        };

        const demoGroups = [
            {
                id: 1,
                name: 'Demo YazÄ±lÄ±m Ekibi',
                member_count: 5,
                created_by: 'Admin',
                created_at: new Date().toISOString(),
                invite_code: 'DEMO123'
            },
            {
                id: 2,
                name: 'Demo TasarÄ±m Grubu',
                member_count: 3,
                created_by: 'ModeratÃ¶r',
                created_at: new Date().toISOString(),
                invite_code: 'DEMO456'
            }
        ];

        setUser(demoUser);
        setIsDemoMode(true);
        setGroups(demoGroups);
        setError('Backend baÄŸlantÄ±sÄ± kurulamadÄ±. Demo veriler gÃ¶steriliyor.');
    };

    const testBackendConnection = async () => {
        try {
            setLoading(true);
            console.log('ðŸ§ª Backend baÄŸlantÄ±sÄ± test ediliyor...');

            const token = localStorage.getItem('token');
            console.log('ðŸ” Test iÃ§in token:', token ? 'âœ… VAR' : 'âŒ YOK');

            if (!token) {
                setError('Token bulunamadÄ±. Demo modda devam ediliyor.');
                handleDemoMode();
                return;
            }

            try {
                // Backend baÄŸlantÄ±sÄ±nÄ± test et
                console.log('ðŸ“¡ Backend isteÄŸi gÃ¶nderiliyor...');
                const response = await authAPI.checkAuth();
                console.log('âœ… Backend baÄŸlantÄ±sÄ± baÅŸarÄ±lÄ±', response.data);

                setUser(response.data);
                setIsDemoMode(false);
                setError(null);

                // GruplarÄ± yÃ¼kle
                await loadGroups();
                console.log('âœ… GerÃ§ek moda geÃ§ildi');

            } catch (backendError) {
                console.error('âŒ Backend hatasÄ±:', backendError);
                throw new Error('Backend baÄŸlantÄ±sÄ± kurulamadÄ±');
            }

        } catch (error) {
            console.error('âŒ Backend baÄŸlantÄ±sÄ± baÅŸarÄ±sÄ±z:', error);
            setIsDemoMode(true);
            setError('Backend baÄŸlantÄ±sÄ± kurulamadÄ±. Demo moda geÃ§ildi.');
            handleDemoMode();
        } finally {
            setLoading(false);
        }
    };

    const createGroup = async (e) => {
        e.preventDefault();
        if (!newGroupName.trim()) return;

        try {
            const demoGroup = {
                id: Date.now(),
                name: newGroupName,
                member_count: 1,
                created_by: user?.username || 'Demo KullanÄ±cÄ±',
                created_at: new Date().toISOString(),
                invite_code: `DEMO${Math.random().toString(36).substring(2, 8).toUpperCase()}`
            };
            setGroups(prev => [...prev, demoGroup]);
            setNewGroupName('');

            if (isDemoMode) {
                alert('âœ… Demo grup oluÅŸturuldu!');
            } else {
                alert('âœ… Grup oluÅŸturuldu!');
            }
        } catch (error) {
            console.error('âŒ Grup oluÅŸturulamadÄ±:', error);
            alert('âŒ Grup oluÅŸturulamadÄ±!');
        }
    };

    const joinGroup = async (groupId) => {
        if (isDemoMode) {
            alert('âœ… Demo mod: Gruba katÄ±ldÄ±nÄ±z!');
        } else {
            alert('âœ… Gruba katÄ±ldÄ±nÄ±z!');
        }
    };

    const leaveGroup = async (groupId) => {
        if (isDemoMode) {
            alert('âœ… Demo mod: Gruptan ayrÄ±ldÄ±nÄ±z!');
        } else {
            alert('âœ… Gruptan ayrÄ±ldÄ±nÄ±z!');
        }
    };

    const joinByInviteCode = async () => {
        if (!inviteCode.trim()) {
            alert('âŒ LÃ¼tfen bir davet kodu girin!');
            return;
        }

        if (isDemoMode) {
            alert(`âœ… Demo mod: ${inviteCode} kodlu gruba katÄ±ldÄ±nÄ±z!`);
        } else {
            alert(`âœ… ${inviteCode} kodlu gruba katÄ±ldÄ±nÄ±z!`);
        }
        setInviteCode('');
    };

    const sendInvites = async (groupId, emails) => {
        if (isDemoMode) {
            alert('âœ… Demo mod: Davetler gÃ¶nderildi!');
        } else {
            alert('âœ… Davetler gÃ¶nderildi!');
        }
        setShowInviteModal(false);
        setInviteEmails('');
    };

    const openInviteModal = (group) => {
        setSelectedGroup(group);
        setShowInviteModal(true);
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('tr-TR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric'
        });
    };

    const loadGroups = async () => {
        if (isDemoMode) {
            console.log('ðŸŽ­ Demo mode, skipping real groups load');
            return;
        }

        try {
            console.log('ðŸ“¡ Loading groups from API...');
            const response = await groupAPI.getGroups();
            setGroups(response.data);
            console.log('âœ… Groups loaded:', response.data.length);
        } catch (error) {
            console.error('âŒ Groups load failed:', error);
            throw error;
        }
    };

    // Debug iÃ§in token durumunu gÃ¶ster
    const getTokenStatus = () => {
        const token = localStorage.getItem('token');
        return {
            exists: !!token,
            length: token ? token.length : 0,
            preview: token ? `${token.substring(0, 10)}...` : 'None'
        };
    };

    if (loading) {
        return (
            <div className="group-manager-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p className="loading-text">YÃ¼kleniyor...</p>
                    <p className="loading-subtext">Kimlik doÄŸrulama kontrol ediliyor</p>
                </div>
            </div>
        );
    }

    const tokenStatus = getTokenStatus();

    return (
        <div className="group-manager-container">
            {/* Durum Bilgisi */}
            <div className={`status-card ${isDemoMode ? 'demo-mode' : 'real-mode'}`}>
                <div className="status-header">
                    <h2 className="status-title">
                        Grup YÃ¶netimi
                        {isDemoMode ? ' - DEMO MOD' : ' - GERÃ‡EK MOD'}
                    </h2>
                    <div className="status-info">
                        <div className={`status-badge ${isDemoMode ? 'demo-mode' : 'real-mode'}`}>
                            <div className="status-dot"></div>
                            {isDemoMode ? 'Demo Modu' : 'GerÃ§ek Mod'}
                        </div>
                        <p className="status-description">
                            {isDemoMode
                                ? 'Backend baÄŸlantÄ±sÄ± yok. Demo veriler gÃ¶steriliyor.'
                                : 'Backend baÄŸlantÄ±sÄ± baÅŸarÄ±lÄ±. GerÃ§ek veriler yÃ¼kleniyor.'}
                        </p>
                    </div>
                </div>
                <button
                    onClick={testBackendConnection}
                    className="test-connection-btn"
                    disabled={loading}
                >
                    <i className="fas fa-plug"></i>
                    {loading ? 'Test Ediliyor...' : 'BaÄŸlantÄ±yÄ± Test Et'}
                </button>
            </div>

            {/* KullanÄ±cÄ± bilgisi */}
            {user && (
                <div className="user-card">
                    <div className="user-avatar">
                        <i className="fas fa-user"></i>
                    </div>
                    <div className="user-info">
                        <h3 className="user-greeting">
                            HoÅŸ geldin, <span className="user-name">{user.username}</span>
                            {isDemoMode && <span className="demo-tag">Demo</span>}
                        </h3>
                        <p className="user-email">{user.email}</p>
                    </div>
                </div>
            )}

            {error && (
                <div className="error-card">
                    <p>{error}</p>
                </div>
            )}

            {/* Grup oluÅŸturma formu */}
            <div className="create-group-card">
                <h3 className="card-title">
                    Yeni Grup OluÅŸtur
                    {isDemoMode && <span className="demo-tag">Demo</span>}
                </h3>
                <form onSubmit={createGroup} className="create-group-form">
                    <input
                        type="text"
                        value={newGroupName}
                        onChange={(e) => setNewGroupName(e.target.value)}
                        placeholder="Grup adÄ±nÄ± yazÄ±n..."
                        className="group-input"
                    />
                    <button
                        type="submit"
                        className="create-group-btn"
                    >
                        <i className="fas fa-plus"></i> OluÅŸtur
                    </button>
                </form>
            </div>

            {/* Grup listesi */}
            <div className="groups-container">
                <div className="groups-header">
                    <div className="groups-title-section">
                        <h3 className="groups-title">
                            {isDemoMode ? 'Demo Gruplar' : 'Gruplar'}
                        </h3>
                        <p className="groups-count">{groups.length} grup bulundu</p>
                    </div>
                    {isDemoMode && (
                        <span className="demo-tag large">Demo Modu</span>
                    )}
                </div>

                {/* Davet kodu ile katÄ±lma */}
                <div className="join-by-code-card">
                    <h3 className="card-title">
                        Davet Kod ile KatÄ±l
                        {isDemoMode && <span className="demo-tag">Demo</span>}
                    </h3>
                    <div className="join-code-form">
                        <input
                            type="text"
                            value={inviteCode}
                            onChange={(e) => setInviteCode(e.target.value)}
