// Dashboard.jsx (Final Fix)
import React, { useState, useEffect, useCallback, useRef } from "react";
import { useAuth } from "../AuthContext";
import { useMemory } from "./MemoryContext";
import ChunkDownloader from "./ChunkDownloader";
import GroupManager from './GroupManager';
import ChatBox from "./ChatBox";
import ChatHeader from "./ChatHeader";
import UserList from "./UserList";
import TrendingView from './TrendingView';
import "./styles/Dashboard.css";
import SearchEngine from './SearchEngine';

// Backend adresi (Resimlerin doru y羹klenmesi i癟in art)
const API_BASE = "http://localhost:8001";

const MemoryStatsView = ({ stats }) => {
    if (!stats) return <div className="loading-state">Haf覺za 襤statistikleri y羹kleniyor...</div>;

    const {
        total_items,
        memory_quota_mb,
        memory_used_mb,
        memory_remaining_mb,
        tier_distribution
    } = stats;

    // NaN Hatas覺 繹z羹m羹: Kota 0 ise b繹lme ilemi yapma
    const usedPercentage = memory_quota_mb > 0
        ? ((memory_used_mb / memory_quota_mb) * 100).toFixed(1)
        : "0.0";

    return (
        <div className="memory-stats-view">
            <h2> Yapay Haf覺za Durumu</h2>

            <div className="stat-card quota-card">
                <h3>Kullan覺m ve Kota</h3>
                <div className="progress-bar-container">
                    <div
                        className="progress-bar"
                        style={{ width: `${usedPercentage}%` }}
                    >
                        {usedPercentage}%
                    </div>
                </div>
                <p>Toplam Kullan覺lan: <strong>{memory_used_mb.toFixed(2)} MB</strong> / {memory_quota_mb.toFixed(2)} MB</p>
                <p>Kalan Kota: <strong>{memory_remaining_mb.toFixed(2)} MB</strong></p>
                <p>Toplam Kay覺tl覺 e: <strong>{total_items}</strong></p>
            </div>

            <div className="stat-card tier-card">
                <h3>Katman Da覺l覺m覺</h3>
                <ul className="tier-list">
                    {tier_distribution && tier_distribution.map((tier, index) => (
                        <li key={index}>
                            <span className="tier-name">{tier.memory_tier__name.toUpperCase()}</span>
                            <span className="tier-count">{tier.count} e</span>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

const WelcomeChatSection = ({ username, onFileSelect, onRefreshTimeline }) => {
    const [inputText, setInputText] = useState("");
    const [isProcessing, setIsProcessing] = useState(false);
    const [aiResponse, setAiResponse] = useState(null);
    const [foundMemories, setFoundMemories] = useState([]);

    const handleKeyDown = async (e) => {
        if (e.key === 'Enter' && !isProcessing && inputText.trim()) {
            await handleSendMessage();
        }
    };

    const handleSendMessage = async () => {
        setIsProcessing(true);
        setAiResponse(null);
        setFoundMemories([]);

        try {
            const token = localStorage.getItem('token');
            // Timeout ekliyoruz (Backend yavasa hata f覺rlatmas覺n, beklesin)
            const controller = new AbortController();
            const timeoutId = setTimeout(() => controller.abort(), 15000); // 15 saniye bekleme s羹resi

            const response = await fetch(`${API_BASE}/api/chat/ask/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ message: inputText }),
                signal: controller.signal
            });
            clearTimeout(timeoutId);

            const data = await response.json();

            if (response.ok) {
                setAiResponse(data.reply);
                if (data.relevant_memories && data.relevant_memories.length > 0) {
                    setFoundMemories(data.relevant_memories);
                }
                if (onRefreshTimeline) onRefreshTimeline();
            } else {
                setAiResponse("zg羹n羹m, bir sorun olutu.");
            }
        } catch (error) {
            console.error("AI Hatas覺:", error);
            if (error.name === 'AbortError') {
                setAiResponse("襤lem zaman a覺m覺na urad覺. Backend youn olabilir.");
            } else {
                setAiResponse("Balant覺 hatas覺 olutu. L羹tfen tekrar deneyin.");
            }
        } finally {
            setIsProcessing(false);
            setInputText("");
        }
    };

    return (
        <div className="welcome-chat-wrapper">
            <div className="welcome-header">
                <img src="/logo-tri.png" alt="Qyptos AI" className="welcome-logo-img" />
                <h1>Merhaba {username || 'Kullan覺c覺'}</h1>
            </div>

            <div className="gemini-input-container">
                <button className="gemini-icon-btn file-btn">
                    <i className="fas fa-plus"></i>
                </button>

                <input
                    type="text"
                    placeholder="Qyptos AI'a sorun (rn: 'Elma resmini bul')"
                    className="gemini-text-input"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    onKeyDown={handleKeyDown}
                    disabled={isProcessing}
                />

                <button className={`gemini-icon-btn mic-btn ${isProcessing ? 'recording' : ''}`} onClick={handleSendMessage}>
                    <i className={`fas ${isProcessing ? 'fa-spinner fa-spin' : 'fa-paper-plane'}`}></i>
                </button>
            </div>

            {(aiResponse || foundMemories.length > 0) && (
                <div className="ai-result-area" style={{ maxWidth: '800px', margin: '20px auto', width: '100%' }}>
                    {aiResponse && (
                        <div className="ai-text-bubble" style={{
                            background: 'var(--bg-card)', padding: '15px', borderRadius: '12px',
                            border: '1px solid var(--border-color)', color: 'var(--text-primary)', marginBottom: '15px'
                        }}>
                            <i className="fas fa-robot" style={{ marginRight: '10px', color: 'var(--accent-blue)' }}></i>
                            {aiResponse}
                        </div>
                    )}
                    {foundMemories.map((mem) => (
                        <MemoryResultCard key={mem.id} memory={mem} />
                    ))}
                </div>
            )}
        </div>
    );
};

const MemoryResultCard = ({ memory }) => {
    // Resim URL'sini d羹zelt (Ba覺na http://localhost:8001 ekle)
    let imageUrl = memory.thumbnail;
    if (imageUrl && !imageUrl.startsWith('http')) {
        imageUrl = `${API_BASE}${imageUrl}`;
    }

    const isImage = memory.file_type === 'image' || memory.file_name.match(/\.(jpg|jpeg|png|gif)$/i);

    // Karta t覺klay覺nca resmi yeni sekmede a癟
    const handleCardClick = () => {
        if (imageUrl) window.open(imageUrl, '_blank');
    };

    return (
        <div className="memory-result-card" onClick={handleCardClick} style={{
            background: 'var(--bg-card)', border: '1px solid var(--accent-blue)', borderRadius: '12px',
            padding: '15px', marginTop: '15px', display: 'flex', gap: '15px', cursor: 'pointer', transition: 'transform 0.2s'
        }}>
            <div className="memory-thumb" style={{ width: '80px', height: '80px', borderRadius: '8px', overflow: 'hidden', background: '#000', flexShrink: 0 }}>
                {isImage ? (
                    <img src={imageUrl} alt={memory.file_name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} onError={(e) => { e.target.style.display = 'none' }} />
                ) : (
                    <div style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '2rem', color: '#fff' }}>
                        <i className={memory.file_type === 'video' ? "fas fa-video" : "fas fa-file-alt"}></i>
                    </div>
                )}
            </div>
            <div className="memory-info" style={{ flex: 1 }}>
                <h4 style={{ margin: '0 0 5px 0', color: 'var(--accent-blue)', fontSize: '1rem' }}>{memory.file_name}</h4>
                <p style={{ margin: 0, fontSize: '0.85rem', color: 'var(--text-secondary)' }}>{memory.summary || "襤癟erik analizi mevcut deil."}</p>
                <div style={{ marginTop: '8px', fontSize: '0.75rem', opacity: 0.8 }}>
                    <span><i className="fas fa-bullseye"></i> %{(memory.similarity_score * 100).toFixed(0)} Eleme</span>
                </div>
            </div>
        </div>
    );
};

const ChronologyBar = ({ refreshTrigger }) => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const fetchTimeline = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`${API_BASE}/api/timeline/?days=7&limit=10`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                const data = await response.json();
                if (data.timeline_events) setEvents(data.timeline_events);
            } catch (error) { console.error("Timeline error:", error); }
        };
        fetchTimeline();
    }, [refreshTrigger]);

    return (
        <div className="chronology-wrapper">
            <h3 style={{ fontSize: '1rem', color: 'var(--text-secondary)', marginBottom: '15px' }}>Son Aktiviteler</h3>
            <div className="chronology-track" style={{ overflowX: 'auto', paddingBottom: '10px', display: 'flex', gap: '20px' }}>
                {events.map((event, index) => (
                    <div key={index} className="chrono-item" style={{ minWidth: '100px', textAlign: 'center' }}>
                        <div className={`chrono-dot ${index === 0 ? 'active' : ''}`} style={{ margin: '0 auto 10px auto' }}></div>
                        <span style={{ fontSize: '0.8rem', display: 'block', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', maxWidth: '100px' }}>
                            {event.title.length > 15 ? event.title.substring(0, 12) + '..' : event.title}
                        </span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default function Dashboard({ notifications = [] }) {
    const { user, logout } = useAuth();
    const { fetchMemoryStats: fetchMemoryStatsFromContext } = useMemory();
    const [memoryStats, setMemoryStats] = useState(null);
    const [storageStats, setStorageStats] = useState({ used: 0, total: 50 });
    const [timelineRefresh, setTimelineRefresh] = useState(0);
    const [activeTab, setActiveTab] = useState("downloader");
    const [notificationCount] = useState(notifications.length || 3);
    const [theme, setTheme] = useState('dark');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const fetchMemoryStats = useCallback(async () => {
        try {
            const stats = await fetchMemoryStatsFromContext();
            setMemoryStats(stats || { memory_used_mb: 0, memory_quota_mb: 100, total_items: 0, tier_distribution: [] });
        } catch {
            setMemoryStats({ memory_used_mb: 0, memory_quota_mb: 100, total_items: 0, tier_distribution: [] });
        }
    }, [fetchMemoryStatsFromContext]);

    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);
        fetchMemoryStats();
        // Basit Storage Mock'u
        setStorageStats({ used: 0.5, total: 50 });
    }, [theme, fetchMemoryStats]);

    const renderContent = () => {
        if (activeTab === "downloader") {
            const memoryPercent = (memoryStats && memoryStats.memory_quota_mb > 0)
                ? ((memoryStats.memory_used_mb / memoryStats.memory_quota_mb) * 100).toFixed(1)
                : "0.0";

            return (
                <div className="dashboard-home-layout">
                    <WelcomeChatSection
                        username={user?.username}
                        onRefreshTimeline={() => setTimelineRefresh(p => p + 1)}
                    />
                    <ChronologyBar refreshTrigger={timelineRefresh} />

                    <div className="stats-grid-visual">
                        <div className="qyptos-card memory-card">
                            <div className="card-header"><i className="fas fa-brain"></i><h3>Yapay Haf覺za</h3></div>
                            <div className="circle-chart-wrapper">
                                <svg viewBox="0 0 36 36" className="circular-chart purple">
                                    <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                    <path className="circle" strokeDasharray={`${memoryPercent}, 100`} d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                    <text x="18" y="20.35" className="percentage">{memoryPercent}%</text>
                                </svg>
                            </div>
                            <div className="card-footer"><span>{memoryStats?.total_items || 0} Bilgi</span></div>
                        </div>
                        {/* Depolama kart覺 vb. buraya gelir */}
                    </div>
                    <div className="downloader-container-wrapper">
                        <ChunkDownloader apiBase={`${API_BASE}/api`} />
                    </div>
                </div>
            );
        }
        // Dier tablar...
        if (activeTab === "chat") return <div className="content-placeholder">Chat Mod羹l羹</div>;
        return <div className="content-placeholder">Se癟im yap覺n</div>;
    };

    return (
        <div className="dashboard-horizontal">
            <nav className="top-nav">
                <div className="nav-left">
                    <div className="logo"><img src="/qyptos-logo.png" className="qyptos-logo-img" alt="logo" /></div>
                    <div className="nav-menu">
                        <div className={`nav-item ${activeTab === "downloader" ? "active" : ""}`} onClick={() => setActiveTab("downloader")}><i className="fas fa-cloud"></i> Bulutum</div>
                    </div>
                </div>
                <div className="nav-right">
                    <div className="theme-toggle" onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}><i className={`fas ${theme === 'dark' ? 'fa-moon' : 'fa-sun'}`}></i></div>
                </div>
            </nav>
            <main className="main-content-horizontal">{renderContent()}</main>
        </div>
    );
}
