// SecureViewer.jsx - D√úZELTƒ∞LMƒ∞≈û
import React, { useEffect, useRef, useState } from "react";

const SecureViewer = ({ file, children, user, isMediaReady = false }) => {
    // ‚úÖ Sadece one_time_view dosyalarda korumayƒ± aktifle≈ütir
    if (!file?.one_time_view) {
        return <>{children}</>;
    }    const [securityBreach, setSecurityBreach] = useState(false);
    const [debugInfo, setDebugInfo] = useState("Ba≈ülatƒ±lƒ±yor...");
    const [isReady, setIsReady] = useState(false);
    const containerRef = useRef(null);
    const [isActive, setIsActive] = useState(false);

    // Basit state: sayacƒ±n zaman penceresi i√ßinde resetlenmesi
    const violationStateRef = useRef({
        count: 0,
        lastTimestamps: [] // UNIX ms
    });
    const MAX_VIOLATIONS = 3;
    const WINDOW_MS = 5000; // aynƒ± 5s i√ßindeki ihlaller sayƒ±lacak

    useEffect(() => {
        const startupTimer = setTimeout(() => {
            setIsReady(true);
            setDebugInfo("‚úÖ G√ºvenlik Sistemi Hazƒ±rlandƒ± ve Kontroller Ba≈ülatƒ±ldƒ±.");
        }, 300);
        return () => clearTimeout(startupTimer);
    }, []);

    useEffect(() => {
        if (isMediaReady) {
            setIsActive(true);
        }
    }, [isMediaReady]);

    useEffect(() => {
        if (!file?.one_time_view) {
            setDebugInfo("‚ùå one_time_view: false - Koruma GEREKSIZ");
            return;
        }

        setDebugInfo("‚úÖ Geli≈ümi≈ü Koruma AKTƒ∞F");

        
        const addViolation = (reason) => {
            const now = Date.now();
            const state = violationStateRef.current;
            // temizle eski timestamp'leri
            state.lastTimestamps = state.lastTimestamps.filter(ts => now - ts <= WINDOW_MS);
            state.lastTimestamps.push(now);
            state.count = state.lastTimestamps.length;
            setDebugInfo(`üö® ƒ∞hlal ${state.count}/${MAX_VIOLATIONS}: ${reason}`);

            if (state.count >= MAX_VIOLATIONS) {
                // kalƒ±cƒ± engelleme
                /*
                setSecurityBreach(true);
                setDebugInfo("üö® MAKSIMUM IHLAL - Icerik engellendi!");
                */
            }
        };

        const detectScreenshotAttempt = (e) => {
            // sadece hazƒ±rsa i≈ülem yap
            if (!isReady) return;

            const code = e.keyCode || e.which || 0;
            const key = e.key || '';

            // PrintScreen, F12, Ctrl+Shift+I/J/C
            const isPrint = code === 44 || key === 'PrintScreen' || key === 'Snapshot';
            const isF12 = code === 123 || key === 'F12';
            const isDevShortcut = (e.ctrlKey && e.shiftKey && ['I', 'J', 'C'].includes((key || '').toUpperCase())) ||
                (e.ctrlKey && e.shiftKey && [73, 74, 67].includes(code));

            if (isPrint || isF12 || isDevShortcut) {
               // e.preventDefault();
                // e.stopPropagation();
                console.log(`üö® EKRAN GORUNTUSU DENEMESI: ${key || code}`);
                // overlay yerine controlled state
               // addViolation(isPrint ? "PrtScn" : isF12 ? "F12" : "DevTools Kƒ±sayolu");
            }
        };

        // Pencere odak kaybƒ±: sadece arka plan/ba≈üka pencereye ge√ßi≈ü ger√ßek bir ihlal sayƒ±lmalƒ±
        let blurTimer = null;
        const handleWindowBlur = () => {
            if (!isReady) return;
            // kƒ±sa odak kayƒ±plarƒ±nƒ± yoksay (√∂r: 300ms altƒ±ndaki)
            if (blurTimer) clearTimeout(blurTimer);
            blurTimer = setTimeout(() => {
                /* addViolation("Pencere Odaƒüƒ± Kaybƒ±");
                */
            }, 400); // 400ms altƒ±nda ge√ßici odak deƒüi≈üimleri yoksayƒ±lƒ±yor
        };

        /* const handleWindowFocus = () => {
            if (blurTimer) {
                clearTimeout(blurTimer);
                blurTimer = null;
            }
        };
        */

        <button onClick={() => {
            violationStateRef.current = { count: 0, lastTimestamps: [] };
            setSecurityBreach(false);
        }}>
            G√ºvenliƒüi Sƒ±fƒ±rla
        </button>

        const handleVisibilityChange = () => {
            if (!isReady) return;
            if (document.hidden) {
               /* addViolation("Sayfa Gizlendi/Arka Plana Alƒ±ndƒ±");
               */
            }
        };

        const handleContextMenu = (e) => {
            // saƒü tƒ±k a√ßƒ±lmasƒ±nƒ± engelle - bu tek ba≈üƒ±na ihlal sayƒ±lmaz, ama logla
            e.preventDefault();
            e.stopPropagation();
            setDebugInfo("‚ö†Ô∏è Saƒü tƒ±k engellendi");
        };

        const eventOptions = { capture: true, passive: false };

        document.addEventListener('keydown', detectScreenshotAttempt, eventOptions);
        document.addEventListener('keyup', detectScreenshotAttempt, eventOptions);
        window.addEventListener('blur', handleWindowBlur);
        window.addEventListener('focus', handleWindowFocus);
        document.addEventListener('visibilitychange', handleVisibilityChange);
        document.addEventListener('contextmenu', handleContextMenu, true);

        return () => {
            document.removeEventListener('keydown', detectScreenshotAttempt, eventOptions);
            document.removeEventListener('keyup', detectScreenshotAttempt, eventOptions);
            window.removeEventListener('blur', handleWindowBlur);
            window.removeEventListener('focus', handleWindowFocus);
            document.removeEventListener('visibilitychange', handleVisibilityChange);
            document.removeEventListener('contextmenu', handleContextMenu, true);
            if (blurTimer) clearTimeout(blurTimer);
        };
    }, [file?.one_time_view, isActive]);

    if (securityBreach) {
        return (
            <div style={{
                width: '100%',
                height: '400px',
                background: 'linear-gradient(45deg, #000, #222)',
                color: '#ff4444',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flexDirection: 'column',
                borderRadius: '8px',
                border: '3px solid #ff4444',
                textAlign: 'center',
                padding: '20px'
            }}>
                <div style={{ fontSize: '48px', marginBottom: '20px' }}>üö´</div>
                <h2 style={{ margin: '0 0 15px 0' }}>G√úVENLƒ∞K ƒ∞HLALƒ∞</h2>
                <p style={{ margin: '0 0 10px 0' }}>Bu i√ßerik g√ºvenlik nedeniyle engellendi.</p>
                <p style={{ margin: 0, fontSize: '14px', opacity: 0.8 }}>{debugInfo}</p>
            </div>
        );
    }

    return (
        <div
            ref={containerRef}
            className={`secure-content ${securityBreach ? 'security-breach-active' : ''}`}
            style={{
                position: 'relative',
                border: file?.one_time_view ? '3px solid #ff4444' : 'none',
                borderRadius: '8px',
                overflow: 'hidden',
                background: file?.one_time_view ? '#1a1a1a' : 'transparent',
                padding: file?.one_time_view ? '10px' : '0',
                userSelect: 'none'
            }}
        >
            {process.env.NODE_ENV === 'development' && (
                <div style={{
                    position: 'absolute',
                    top: '5px',
                    left: '5px',
                    background: 'rgba(0,0,0,0.8)',
                    color: '#00ff00',
                    padding: '5px',
                    borderRadius: '3px',
                    fontSize: '10px',
                    zIndex: 10000,
                    fontFamily: 'monospace'
                }}>
                    üõ°Ô∏è {debugInfo}
                </div>
            )}

            {children}

            {file?.one_time_view && (
                <>
                    <div style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        background: `repeating-linear-gradient(45deg, rgba(255,0,0,0.05), rgba(255,0,0,0.05) 10px, rgba(0,0,255,0.03) 10px, rgba(0,0,255,0.03) 20px)`,
                        pointerEvents: 'none',
                        zIndex: 9998
                    }} />

                    <div style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%) rotate(-45deg)',
                        color: 'rgba(255,255,255,0.12)',
                        fontSize: '48px',
                        fontWeight: 'bold',
                        whiteSpace: 'nowrap',
                        pointerEvents: 'none',
                        zIndex: 9997,
                        textAlign: 'center'
                    }}>
                        {user?.username || 'Kullanici'} | {user?.email || ''} | {new Date().toLocaleDateString('tr-TR')}
                    </div>

                    <div style={{
                        position: 'absolute',
                        bottom: '10px',
                        right: '10px',
                        color: 'rgba(255,255,255,0.6)',
                        fontSize: '12px',
                        background: 'rgba(0,0,0,0.5)',
                        padding: '5px 10px',
                        borderRadius: '3px',
                        pointerEvents: 'none',
                        zIndex: 9999
                    }}>
                        {user?.username || 'Kullanici'} | {new Date().toLocaleString('tr-TR')}
                    </div>
                </>
            )}
        </div>
    );
};

export default SecureViewer;
