import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../AuthContext";
import { useMemory } from "./MemoryContext";
import ChunkDownloader from "./ChunkDownloader";
import GroupManager from './GroupManager';
import ChatBox from "./ChatBox";
import ChatHeader from "./ChatHeader";
import UserList from "./UserList";
import TrendingView from './TrendingView';
import "./styles/Dashboard.css";
import SearchEngine from './SearchEngine';

// --- YENİ BİLEŞEN: KRONOLOJİ ÇUBUĞU ---
const ChronologyBar = () => {
    const [activeTab, setActiveTab] = useState('recent');

    const tabs = [
        { id: 'recent', label: 'En Son Yapılan Aktiviteler' },
        { id: 'recurrent', label: 'Tekrarladıkların' },
        { id: 'suggestions', label: 'Son İşlemlere Göre Öneriler' }
    ];

    return (
        <div className="chronology-wrapper">
            <div className="chronology-track">
                {tabs.map(tab => (
                    <div
                        key={tab.id}
                        className={`chrono-item ${activeTab === tab.id ? 'active' : ''}`}
                        onClick={() => setActiveTab(tab.id)}
                    >
                        <div className="chrono-dot"></div>
                        <span>{tab.label}</span>
                    </div>
                ))}
                <div className="track-line"></div>
            </div>

            {/* Alt kısımda seçili sekmeye göre ufak bir içerik gösterimi (Opsiyonel) */}
            <div className="chrono-content-preview">
                {activeTab === 'recent' && <p>Son yüklenen dosya: <strong>System_Log_v2.csv</strong></p>}
                {activeTab === 'recurrent' && <p>Sık kullanılan: <strong>Hafıza Temizliği</strong></p>}
                {activeTab === 'suggestions' && <p>Öneri: <strong>Veri sıkıştırma işlemini başlat.</strong></p>}
            </div>
        </div>
    );
};

// --- YENİ BİLEŞEN: ALT CHAT BAR ---
const BottomChatBar = () => {
    return (
        <div className="bottom-chat-wrapper">
            <div className="bottom-chat-container">
                <input type="text" placeholder="Buraya yazın..." className="bottom-chat-input" />
                <button className="bottom-chat-send">
                    <i className="fas fa-paper-plane"></i>
                </button>
            </div>
        </div>
    );
};

export default function Dashboard({ notifications = [] }) {
    const { user, logout } = useAuth();
    const { fetchMemoryStats: fetchMemoryStatsFromContext } = useMemory();
    const [memoryStats, setMemoryStats] = useState(null);
    const [activeTab, setActiveTab] = useState("downloader");
    const [notificationCount] = useState(notifications.length || 3);
    const [selectedThread, setSelectedThread] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [theme, setTheme] = useState('dark');

    // Memory Stats Fetch (Aynı Mantık)
    const fetchMemoryStats = useCallback(async () => {
        try {
            if (fetchMemoryStatsFromContext) {
                const stats = await fetchMemoryStatsFromContext();
                setMemoryStats(stats);
            } else {
                // Mock data for visual consistency with image if API fails
                setMemoryStats({
                    memory_used_mb: 2.5,
                    memory_quota_mb: 100,
                    total_items: 42
                });
            }
        } catch (error) {
            console.error('Hata:', error);
            setMemoryStats({ memory_used_mb: 0, memory_quota_mb: 100 });
        }
    }, [fetchMemoryStatsFromContext]);

    const handleLogout = () => logout();

    const toggleTheme = () => {
        const newTheme = theme === 'dark' ? 'light' : 'dark';
        setTheme(newTheme);
        document.documentElement.setAttribute('data-theme', newTheme);
    };

    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);
        fetchMemoryStats();
        // User fetch logic here...
    }, [theme, fetchMemoryStats]);

    // Ana İçerik Render
    const renderContent = () => {
        switch (activeTab) {
            case "downloader":
                return (
                    <div className="dashboard-home-layout">
                        {/* Mavi Gradient Header Kartı */}
                        <div className="hero-card">
                            <div className="hero-content">
                                <h2><i className="fas fa-cloud"></i> Bulutum - Dosya Yöneticisi</h2>
                                <p>Dosyalarınızı yükleyin, yönetin ve Qyptos güvenliğiyle saklayın.</p>
                            </div>
                        </div>

                        {/* 1. KRONOLOJİ ÇUBUĞU */}
                        <ChronologyBar />

                        {/* 2. DEPOLAMA VE HAFIZA KARTLARI */}
                        <div className="stats-grid-visual">

                            {/* Depolama Kartı */}
                            <div className="qyptos-card storage-card">
                                <div className="card-header">
                                    <i className="fas fa-archive"></i>
                                    <h3>Depolama Alanı</h3>
                                </div>
                                <div className="circle-chart-wrapper">
                                    <svg viewBox="0 0 36 36" className="circular-chart blue">
                                        <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <path className="circle" strokeDasharray="30, 100" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <text x="18" y="20.35" className="percentage">30%</text>
                                    </svg>
                                </div>
                                <div className="card-footer">
                                    <span>Kullanılan: 30 GB</span>
                                    <span>Toplam: 100 GB</span>
                                </div>
                            </div>

                            {/* Yapay Hafıza Kartı */}
                            <div className="qyptos-card memory-card">
                                <div className="card-header">
                                    <i className="fas fa-brain"></i>
                                    <h3>Yapay Hafıza</h3>
                                </div>
                                <div className="circle-chart-wrapper">
                                    <svg viewBox="0 0 36 36" className="circular-chart purple">
                                        <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <path className="circle" strokeDasharray={`${((memoryStats?.memory_used_mb || 0) / (memoryStats?.memory_quota_mb || 100) * 100)}, 100`} d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                        <text x="18" y="20.35" className="percentage">
                                            {memoryStats ? ((memoryStats.memory_used_mb / memoryStats.memory_quota_mb) * 100).toFixed(1) : 0}%
                                        </text>
                                    </svg>
                                </div>
                                <div className="card-footer">
                                    <span>{memoryStats?.total_items || 0} Öğrenilen Bilgi</span>
                                </div>
                            </div>
                        </div>

                        {/* 3. ALT CHAT BAR */}
                        <BottomChatBar />
                    </div>
                );

            // Diğer case'ler aynı kalabilir veya görseldeki header yapısına uyarlanabilir
            case "groups": return <GroupManager />;
            case "chat": return ( /* Chat Interface Code same as before */ <div className="chat-container">...</div>);
            case "ads": return <TrendingView />;
            case "media": return <SearchEngine />;
            case "profile": return <div className="content-placeholder"><h2>Profil</h2></div>;
            default: return <div className="content-placeholder">Seçim Yapın</div>;
        }
    };

    return (
        <div className="dashboard-horizontal">
            {/* Sidebar SİLİNDİ. Sadece Top Nav var. */}

            {/* Üst Navigasyon Bar - Görseldeki gibi */}
            <nav className="top-nav qyptos-nav">
                <div className="nav-left">
                    <div className="logo">
                        <span className="logo-text">QYPTOS</span>
                    </div>

                    <div className="nav-menu">
                        <div className={`nav-item ${activeTab === "downloader" ? "active" : ""}`} onClick={() => setActiveTab("downloader")}>
                            <i className="fas fa-cloud"></i> <span>Bulutum</span>
                        </div>
                        <div className={`nav-item ${activeTab === "ads" ? "active" : ""}`} onClick={() => setActiveTab("ads")}>
                            <i className="fas fa-fire"></i> <span>Trendler</span>
                        </div>
                        <div className={`nav-item ${activeTab === "media" ? "active" : ""}`} onClick={() => setActiveTab("media")}>
                            <i className="fas fa-search"></i> <span>Ara</span>
                        </div>
                        <div className={`nav-item ${activeTab === "groups" ? "active" : ""}`} onClick={() => setActiveTab("groups")}>
                            <i className="fas fa-users"></i> <span>Gruplar</span>
                        </div>
                        <div className={`nav-item ${activeTab === "chat" ? "active" : ""}`} onClick={() => setActiveTab("chat")}>
                            <i className="fas fa-comments"></i> <span>DM</span>
                        </div>
                    </div>
                </div>

                <div className="nav-right">
                    <div className="theme-toggle" onClick={toggleTheme}>
                        <i className={`fas ${theme === 'dark' ? 'fa-sun' : 'fa-moon'}`}></i>
                    </div>
                    <div className="notification-bell">
                        <i className="fas fa-bell"></i>
                        {notificationCount > 0 && <span className="notification-badge">{notificationCount}</span>}
                    </div>
                    <div className="user-dropdown">
                        <div className="user-avatar"><i className="fas fa-user"></i></div>
                        <span className="user-name">{user?.username || 'burak'}</span>
                        <i className="fas fa-chevron-down"></i>
                        <div className="dropdown-menu">
                            <div className="dropdown-item" onClick={handleLogout}>Çıkış Yap</div>
                        </div>
                    </div>
                </div>
            </nav>

            {/* Ana İçerik */}
            <main className="main-content-horizontal">
                {renderContent()}
            </main>
        </div>
    );
}
