// Dashboard.jsx (Final Fix - G√∂rsel Garantili Versiyon)
import React, { useState, useEffect, useCallback, useRef } from "react";
import { useAuth } from "../AuthContext";
import { useMemory } from "./MemoryContext";
import ChunkDownloader from "./ChunkDownloader";
import GroupManager from './GroupManager';
import ChatBox from "./ChatBox";
import ChatHeader from "./ChatHeader";
import UserList from "./UserList";
import TrendingView from './TrendingView';
import "./styles/Dashboard.css";
import SearchEngine from './SearchEngine';

const API_BASE = "http://localhost:8001"; // Resimler i√ßin Backend adresi

// --- 1. HAFIZA KARTI Bƒ∞LE≈ûENƒ∞ (G√∂rselleri G√∂steren Kƒ±sƒ±m) ---
const MemoryResultCard = ({ memory }) => {
    // G√∂rsel URL'sini d√ºzelt (localhost:8001 ekle)
    let imageUrl = memory.thumbnail;

    // Eƒüer URL "/media" ile ba≈ülƒ±yorsa ba≈üƒ±na API adresini ekle
    if (imageUrl && !imageUrl.startsWith('http')) {
        imageUrl = `${API_BASE}${imageUrl}`;
    }

    // Dosya tipine g√∂re g√∂rsel mi deƒüil mi karar ver
    const isImage = memory.file_type === 'image' ||
        (memory.file_name && memory.file_name.match(/\.(jpg|jpeg|png|gif|webp)$/i));

    return (
        <div className="memory-result-card"
            onClick={() => imageUrl && window.open(imageUrl, '_blank')}
            style={{
                background: 'var(--bg-card)',
                border: '1px solid var(--accent-blue)',
                borderRadius: '12px',
                padding: '15px',
                marginTop: '15px',
                display: 'flex',
                gap: '15px',
                cursor: 'pointer',
                animation: 'fadeIn 0.5s ease'
            }}>

            {/* K√º√ß√ºk Resim Alanƒ± */}
            <div className="memory-thumb" style={{
                width: '80px', height: '80px', borderRadius: '8px',
                overflow: 'hidden', background: '#000', flexShrink: 0,
                display: 'flex', alignItems: 'center', justifyContent: 'center'
            }}>
                {isImage && imageUrl ? (
                    <img
                        src={imageUrl}
                        alt={memory.file_name}
                        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                        onError={(e) => {
                            e.target.style.display = 'none'; // Y√ºklenemezse gizle
                            e.target.parentElement.innerHTML = '<i class="fas fa-image" style="color:white; font-size:24px;"></i>';
                        }}
                    />
                ) : (
                    <div style={{ fontSize: '2rem', color: '#fff' }}>
                        <i className={memory.file_type === 'video' ? "fas fa-video" : "fas fa-file-alt"}></i>
                    </div>
                )}
            </div>

            {/* Bilgi Alanƒ± */}
            <div className="memory-info" style={{ flex: 1 }}>
                <h4 style={{ margin: '0 0 5px 0', color: 'var(--accent-blue)', fontSize: '1rem' }}>
                    {memory.file_name}
                </h4>
                <p style={{ margin: 0, fontSize: '0.85rem', color: 'var(--text-secondary)' }}>
                    {memory.summary || "ƒ∞√ßerik analizi mevcut deƒüil."}
                </p>
                <div style={{ marginTop: '8px', fontSize: '0.75rem', opacity: 0.8, display: 'flex', gap: '15px' }}>
                    <span><i className="fas fa-bullseye"></i> %{(memory.similarity_score * 100).toFixed(0)} E≈üle≈üme</span>
                    <span><i className="fas fa-folder"></i> {memory.file_path ? memory.file_path.split('/').pop() : 'Dosya'}</span>
                </div>
            </div>
        </div>
    );
};

// --- 2. SOHBET B√ñL√úM√ú (Arama Yapƒ±lan Kƒ±sƒ±m) ---
const WelcomeChatSection = ({ username, onFileSelect, onRefreshTimeline }) => {
    const [inputText, setInputText] = useState("");
    const [isProcessing, setIsProcessing] = useState(false);
    const [aiResponse, setAiResponse] = useState(null);
    const [foundMemories, setFoundMemories] = useState([]);

    const handleKeyDown = async (e) => {
        if (e.key === 'Enter' && !isProcessing && inputText.trim()) {
            await handleSendMessage();
        }
    };

    const handleSendMessage = async () => {
        if (!inputText.trim() || isProcessing) return;

        setIsProcessing(true);
        setAiResponse(null);
        setFoundMemories([]);

        // 60 Saniye Zaman A≈üƒ±mƒ±
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 60000);

        try {
            const token = localStorage.getItem('token');
            console.log("üì§ Sorgu g√∂nderiliyor:", inputText);

            const response = await fetch(`${API_BASE}/api/chat/ask/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ message: inputText }),
                signal: controller.signal
            });

            clearTimeout(timeoutId);
            const data = await response.json();
            console.log("üì• Backend Yanƒ±tƒ±:", data);

            if (response.ok) {
                // 1. Cevap Metnini Ayarla
                const replyText = data.reply || "ƒ∞≈ülem tamamlandƒ±.";
                setAiResponse(replyText);

                // 2. Hafƒ±za Kartlarƒ±nƒ± Ayarla (√ñnceki hatanƒ±n d√ºzeltildiƒüi yer)
                const memories = data.relevant_memories || [];
                if (memories.length > 0) {
                    console.log("‚úÖ Ekrana basƒ±lacak g√∂rseller:", memories);
                    setFoundMemories(memories);
                } else {
                    console.log("‚ö†Ô∏è G√∂rsel sonu√ß bo≈ü d√∂nd√º.");
                }

                if (onRefreshTimeline) onRefreshTimeline();
            } else {
                setAiResponse("Hata: " + (data.detail || "Bir sorun olu≈ütu."));
            }
        } catch (error) {
            console.error("AI Hatasƒ±:", error);
            if (error.name === 'AbortError') {
                setAiResponse("ƒ∞≈ülem zaman a≈üƒ±mƒ±na uƒüradƒ±. Backend yanƒ±t vermedi.");
            } else {
                setAiResponse(`Baƒülantƒ± hatasƒ±: ${error.message}`);
            }
        } finally {
            setIsProcessing(false);
            setInputText("");
            clearTimeout(timeoutId);
        }
    };

    return (
        <div className="welcome-chat-wrapper">
            <div className="welcome-header">
                <img src="/logo-tri.png" alt="Qyptos AI" className="welcome-logo-img" />
                <h1>Merhaba {username || 'Kullanƒ±cƒ±'}</h1>
            </div>

            <div className="gemini-input-container">
                <button className="gemini-icon-btn file-btn">
                    <i className="fas fa-plus"></i>
                </button>

                <input
                    type="text"
                    placeholder="Qyptos AI'a sorun (√ñrn: 'Elma resmini bul')"
                    className="gemini-text-input"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    onKeyDown={handleKeyDown}
                    disabled={isProcessing}
                />

                <button
                    className={`gemini-icon-btn mic-btn ${isProcessing ? 'recording' : ''}`}
                    onClick={handleSendMessage}
                    disabled={isProcessing}
                >
                    <i className={`fas ${isProcessing ? 'fa-spinner fa-spin' : 'fa-paper-plane'}`}></i>
                </button>
            </div>

            {/* SONU√á ALANI */}
            {(aiResponse || foundMemories.length > 0) && (
                <div className="ai-result-area" style={{ maxWidth: '800px', margin: '20px auto', width: '100%' }}>
                    {aiResponse && (
                        <div className="ai-text-bubble" style={{
                            background: 'var(--bg-card)', padding: '15px', borderRadius: '12px',
                            border: '1px solid var(--border-color)', color: 'var(--text-primary)', marginBottom: '15px', lineHeight: '1.5'
                        }}>
                            <i className="fas fa-robot" style={{ marginRight: '10px', color: 'var(--accent-blue)' }}></i>
                            {aiResponse}
                        </div>
                    )}

                    {/* Hafƒ±za Kartlarƒ±nƒ± D√∂ng√ºye Sok */}
                    {foundMemories.map((mem) => (
                        <MemoryResultCard key={mem.id || Math.random()} memory={mem} />
                    ))}
                </div>
            )}
        </div>
    );
};

// --- 3. Dƒ∞ƒûER Bƒ∞LE≈ûENLER (Stats, Chronology vs) ---

const MemoryStatsView = ({ stats }) => {
    if (!stats) return <div className="loading-state">Hafƒ±za ƒ∞statistikleri y√ºkleniyor...</div>;
    const { total_items, memory_quota_mb, memory_used_mb, memory_remaining_mb, tier_distribution } = stats;
    const usedPercentage = memory_quota_mb > 0 ? ((memory_used_mb / memory_quota_mb) * 100).toFixed(1) : "0.0";

    return (
        <div className="memory-stats-view">
            <h2>üß† Yapay Hafƒ±za Durumu</h2>
            <div className="stat-card quota-card">
                <h3>Kullanƒ±m ve Kota</h3>
                <div className="progress-bar-container">
                    <div className="progress-bar" style={{ width: `${usedPercentage}%` }}>{usedPercentage}%</div>
                </div>
                <p>Kullanƒ±lan: {memory_used_mb.toFixed(2)} MB / {memory_quota_mb.toFixed(2)} MB</p>
                <p>Toplam √ñƒüe: {total_items}</p>
            </div>
        </div>
    );
};

const ChronologyBar = ({ refreshTrigger }) => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const fetchTimeline = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`${API_BASE}/api/timeline/?days=7&limit=10`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                const data = await response.json();
                if (data.timeline_events) setEvents(data.timeline_events);
            } catch (error) { console.error("Timeline error:", error); }
        };
        fetchTimeline();
    }, [refreshTrigger]);

    return (
        <div className="chronology-wrapper">
            <h3 style={{ fontSize: '1rem', color: 'var(--text-secondary)', marginBottom: '15px' }}>Son Aktiviteler</h3>
            <div className="chronology-track" style={{ overflowX: 'auto', paddingBottom: '10px', display: 'flex', gap: '15px' }}>
                {events.map((event, index) => (
                    <div key={index} className="chrono-item" style={{ minWidth: '120px', textAlign: 'center' }}>
                        <div className={`chrono-dot ${index === 0 ? 'active' : ''}`} style={{ margin: '0 auto 10px auto' }}></div>
                        <span style={{ fontSize: '0.8rem', display: 'block', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', maxWidth: '110px' }}>
                            {event.title.length > 15 ? event.title.substring(0, 12) + '..' : event.title}
                        </span>
                        <small style={{ fontSize: '0.7rem', color: 'gray' }}>{new Date(event.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</small>
                    </div>
                ))}
            </div>
        </div>
    );
};

// --- ANA DASHBOARD ---
export default function Dashboard({ notifications = [] }) {
    const { user, logout } = useAuth();
    const { fetchMemoryStats: fetchMemoryStatsFromContext } = useMemory();
    const [memoryStats, setMemoryStats] = useState(null);
    const [storageStats, setStorageStats] = useState({ used: 0, total: 50 });
    const [timelineRefresh, setTimelineRefresh] = useState(0);
    const [activeTab, setActiveTab] = useState("downloader");
    const [notificationCount] = useState(notifications.length || 3);
    const [theme, setTheme] = useState('dark');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const fetchStorageData = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) return;
            const response = await fetch(`${API_BASE}/api/files/`, { headers: { 'Authorization': `Bearer ${token}` } });
            if (response.ok) {
                const files = await response.json();
                let totalBytes = 0;
                files.forEach(f => totalBytes += (f.file_size || 0));
                setStorageStats({ used: totalBytes / (1024 ** 3), total: 50 });
            }
        } catch (e) { console.error(e); }
    };

    useEffect(() => {
        fetchMemoryStatsFromContext().then(setMemoryStats).catch(() => setMemoryStats({ memory_used_mb: 0, memory_quota_mb: 100 }));
        fetchStorageData();
    }, [fetchMemoryStatsFromContext]);

    // Render Content
    const renderContent = () => {
        if (activeTab === "downloader") {
            const memoryPercent = (memoryStats && memoryStats.memory_quota_mb > 0) ? ((memoryStats.memory_used_mb / memoryStats.memory_quota_mb) * 100).toFixed(1) : "0.0";
            return (
                <div className="dashboard-home-layout">
                    <WelcomeChatSection username={user?.username} onRefreshTimeline={() => setTimelineRefresh(p => p + 1)} />
                    <ChronologyBar refreshTrigger={timelineRefresh} />
                    <div className="stats-grid-visual">
                        <div className="qyptos-card memory-card">
                            <div className="card-header"><i className="fas fa-brain"></i><h3>Yapay Hafƒ±za</h3></div>
                            <div className="circle-chart-wrapper">
                                <svg viewBox="0 0 36 36" className="circular-chart purple">
                                    <path className="circle-bg" d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                    <path className="circle" strokeDasharray={`${memoryPercent}, 100`} d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" />
                                    <text x="18" y="20.35" className="percentage">{memoryPercent}%</text>
                                </svg>
                            </div>
                            <div className="card-footer"><span>{memoryStats?.total_items || 0} Bilgi</span></div>
                        </div>
                    </div>
                    <div className="downloader-container-wrapper"><ChunkDownloader apiBase={`${API_BASE}/api`} /></div>
                </div>
            );
        }
        if (activeTab === "chat") return <div className="chat-container"><ChatBox currentUser={user?.username} /></div>;
        if (activeTab === "ads") return <TrendingView />;
        if (activeTab === "groups") return <GroupManager />;
        if (activeTab === "media") return <SearchEngine />;
        return <div>Se√ßim yapƒ±n</div>;
    };

    return (
        <div className="dashboard-horizontal">
            <nav className="top-nav">
                <div className="nav-left">
                    <div className="logo"><img src="/qyptos-logo.png" className="qyptos-logo-img" alt="logo" /></div>
                    <div className="nav-menu">
                        <div className={`nav-item ${activeTab === "downloader" ? "active" : ""}`} onClick={() => setActiveTab("downloader")}><i className="fas fa-cloud"></i> Bulutum</div>
                        <div className={`nav-item ${activeTab === "ads" ? "active" : ""}`} onClick={() => setActiveTab("ads")}><i className="fas fa-fire"></i> Trendler</div>
                        <div className={`nav-item ${activeTab === "media" ? "active" : ""}`} onClick={() => setActiveTab("media")}><i className="fas fa-search"></i> Ara</div>
                        <div className={`nav-item ${activeTab === "groups" ? "active" : ""}`} onClick={() => setActiveTab("groups")}><i className="fas fa-users"></i> Gruplar</div>
                        <div className={`nav-item ${activeTab === "chat" ? "active" : ""}`} onClick={() => setActiveTab("chat")}><i className="fas fa-comments"></i> DM</div>
                    </div>
                </div>
                <div className="nav-right">
                    <div className="user-dropdown" onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                        <span className="user-name">{user?.username || 'burak'}</span>
