import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useApi } from "../utils/api";
import { Card, CardContent } from "./ui/Card";
import { Button } from "./ui/Button";
import { Input } from "./ui/Input";

export default function FileDetail() {
    const { fileId } = useParams();
    const { apiFetch } = useApi();
    const [file, setFile] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");

    useEffect(() => {
        const loadFile = async () => {
            const res = await apiFetch(`http://127.0.0.1:8000/groups/files/${fileId}/`);
            if (res.ok) {
                const data = await res.json();
                setFile(data.file);
                setComments(data.comments);
            }
        };
        loadFile();
    }, [fileId]);

    const handleAddComment = async () => {
        const res = await apiFetch(`http://127.0.0.1:8000/groups/files/${fileId}/`, {
            method: "POST",
            body: JSON.stringify({ text: newComment }),
        });
        if (res.ok) {
            const data = await res.json();
            setComments((prev) => [data, ...prev]);
            setNewComment("");
        }
    };

    if (!file) return <p>YÃ¼kleniyor...</p>;

    return (
        <div className="p-6">
            <Card className="mb-6">
                <CardContent>
                    <h2 className="text-xl font-bold">{file.filename}</h2>
                    <p className="text-gray-600">ðŸ“¤ YÃ¼kleyen: {file.uploaded_by}</p>
                    <p className="text-gray-500 text-sm">ðŸ“… {new Date(file.created_at).toLocaleString()}</p>
                </CardContent>
            </Card>

            <div className="mb-4 flex gap-2">
                <Input
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Yorum yaz..."
                />
                <Button onClick={handleAddComment}>GÃ¶nder</Button>
            </div>

            <div className="space-y-3">
                {comments.map((c) => (
                    <Card key={c.id}>
                        <CardContent>
                            <p className="font-medium">{c.user}</p>
                            <p>{c.text}</p>
                            <p className="text-xs text-gray-400">
                                {new Date(c.created_at).toLocaleString()}
                            </p>
                        </CardContent>
                    </Card>
                ))}
            </div>
        </div>
    );
}
