import React, { useState } from "react";
// UI bileşenleri aynı klasörde
import { Card, CardHeader, CardContent } from "./Card";
import { Input } from "./Input";
import { Button } from "./Button";
// AuthContext bir üst klasörde (src)
import { useAuth } from "../AuthContext";

const LoginSignup = ({ apiBase }) => {
    const auth = useAuth();

    // Auth context yüklenene kadar bekle
    if (!auth) {
        return <div>Authentication system is loading...</div>;
    }

    const { login } = auth;
    const [mode, setMode] = useState("login");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [name, setName] = useState("");
    const [message, setMessage] = useState("");

    const LoginSignup = ({ apiBase }) => {
        const auth = useAuth();
        const [mode, setMode] = useState("login");
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [name, setName] = useState("");
        const [message, setMessage] = useState("");

        const handleSubmit = async () => {
            const endpoint = mode === "login" ? "login/" : "signup/";
            try {
                const payload = mode === "login"
                    ? { email, password }
                    : { email, password, name };

                const res = await fetch(`${apiBase}/auth/${endpoint}`, {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload),
                });

                const data = await res.json();
                if (res.ok) {
                    auth.login(data.user); // Bu, yönlendirmeyi tetikleyecek
                    setMessage("✅ İşlem başarıyla tamamlandı! Yönlendiriliyorsunuz...");
                } else {
                    setMessage(`❌ ${data.detail || "Bir hata oluştu."}`);
                }
            } catch (err) {
                console.error(err);
                setMessage("❌ Sunucu hatası.");
            }
        };

        return (
            <Card className="max-w-md mx-auto mt-20 shadow-lg">
                <CardHeader>
                    <h2 className="text-2xl font-semibold text-center">
                        {mode === "login" ? "Giriş Yap" : "Kayıt Ol"}
                    </h2>
                </CardHeader>
                <CardContent className="p-6">
                    <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
                        <div className="space-y-4">
                            {mode === "signup" && (
                                <Input
                                    type="text"
                                    placeholder="İsim"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                />
                            )}
                            <Input
                                type="email"
                                placeholder="E-posta"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <Input
                                type="password"
                                placeholder="Şifre"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <Button type="submit" className="w-full">
                                {mode === "login" ? "Giriş Yap" : "Kayıt Ol"}
                            </Button>
                        </div>
                    </form>
                    <div className="text-center text-sm text-gray-500 mt-2">
                        {mode === "login" ? (
                            <>
                                Hesabınız yok?{" "}
                                <button
                                    className="text-blue-500 underline"
                                    onClick={() => setMode("signup")}
                                >
                                    Kayıt Ol
                                </button>
                            </>
                        ) : (
                            <>
                                Hesabınız var mı?{" "}
                                <button
                                    className="text-blue-500 underline"
                                    onClick={() => setMode("login")}
                                >
                                    Giriş Yap
                                </button>
                            </>
                        )}
                    </div>
                    {message && <p className="text-center mt-2">{message}</p>}
                </CardContent>
            </Card>
        );
    }
};
export default LoginSignup;
