import React, { useState } from "react";
import { useAuth } from "../AuthContext";
import { Card, CardHeader, CardContent } from './ui/Card';
import { Input } from "./Input";
import { Button } from './ui/Button';

const LoginSignup = ({ apiBase }) => {
    const auth = useAuth();
    const [mode, setMode] = useState("login");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [name, setName] = useState("");
    const [message, setMessage] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        onKayit(formData);
        const url = `${apiBase}/auth/${mode}/`;
        const endpoint = mode === "login" ? "login/" : "signup/";
        const payload =
            mode === "login"
                ? { username, password }
                : { username, email, password };
        setIsLoading(true);
        setMessage("");

        if (res.ok) {
            const data = await res.json();

            if (mode === "login") {
                auth.login(data.user, data.access, data.refresh);
                setMessage("✅ Giriş başarılı!");
            } else {
                // ✅ Kayıt sonrası otomatik giriş
                auth.login(data.user, data.access, data.refresh);
                setMessage("✅ Kayıt başarılı, otomatik giriş yapıldı.");
            }
        }

        try {
            const res = await fetch(`${apiBase}/auth/${endpoint}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload),
            });
            const data = await res.json();
            if (res.ok) {
                const data = await res.json();
                // örnek: data = { user: {id, email, name}, access: "...", refresh: "..." }
                localStorage.setItem("token", data.access);
                auth.login(data.user);
                setMessage("✅ İşlem başarılı, yönlendiriliyorsunuz...");
            }
 else {
                const errorData = await res.json();
                setMessage(`❌ ${errorData.error || errorData.detail || "Bir hata oluştu."}`);
            }
        } catch (err) {
            console.error("İstek hatası:", err);
            setMessage("❌ Ağ hatası. Sunucuya bağlanılamıyor.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="flex justify-center items-center h-screen w-full"> {/* Arka planı siyah ve formu ortalar */}
            <Card className="max-w-md mx-auto my-12 p-6 bg-white rounded-lg shadow-xl"> {/* Beyaz form kutusu */}
                <CardHeader className="text-center">
                    <h2 className="text-3xl font-bold text-gray-800">
                        {mode === "login" ? "Giriş Yap" : "Kayıt Ol"}
                    </h2>
                </CardHeader>
                <CardContent>
                    {message && <p className="text-center my-4">{message}</p>}
                    <form onSubmit={handleSubmit} className="space-y-4">
                        {mode === "signup" && (
                            <Input
                                type="text"
                                placeholder="İsim"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                className="w-full"
                            />
                        )}
                        <Input
                            type="email"
                            placeholder="E-posta"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full"
                        />
                        <Input
                            type="password"
                            placeholder="Şifre"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full"
                        />
                        <Button
                            type="submit"
                            className="w-full bg-cyan-500 hover:bg-cyan-600 text-white font-bold py-2 px-4 rounded"
                            disabled={isLoading}
                        >
                            {isLoading ? "İşleniyor..." : (mode === "login" ? "Giriş Yap" : "Kayıt Ol")}
                        </Button>
                    </form>
                    <div className="text-center text-sm text-gray-500 mt-4">
                        {mode === "login" ? (
                            <>
                                Hesabınız yok mu?{" "}
                                <button
                                    type="button"
                                    className="text-cyan-500 hover:underline"
                                    onClick={() => setMode("signup")}
                                >
                                    Kayıt Ol
                                </button>
                            </>
                        ) : (
                            <>
                                Zaten bir hesabınız var mı?{" "}
                                <button
                                    type="button"
                                    className="text-cyan-500 hover:underline"
                                    onClick={() => setMode("login")}
                                >
                                    Giriş Yap
                                </button>
                            </>
                        )}
                    </div>
                </CardContent>
            </Card>
        </div>
    );
};

export default LoginSignup;
