import React, { useState } from 'react';

function LoginSignup({ onKayit }) {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Yeni: Tarayıcı çerezlerinden CSRF jetonunu çeken yardımcı fonksiyon
    const getCSRFToken = () => {
        const cookieValue = document.cookie
            .split('; ')
            .find(row => row.startsWith('csrftoken='))
            ?.split('=')[1];
        return cookieValue || '';
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        e.stopPropagation();

        // Jetonu al ve istek başlıklarına ekle
        const csrfToken = getCSRFToken();
        const headers = {
            'Content-Type': 'application/json',
            // **ÖNEMLİ DÜZELTME:** CSRF jetonunu ekle
            'X-CSRFToken': csrfToken,
        };

        try {
            if (isLogin) {
                // LOGIN LOGIC
                console.log('Login attempt:', formData);
                const response = await fetch('http://localhost:8001/api/auth/login/', {
                    method: 'POST',
                    headers: headers, // Düzeltilen headers kullanılıyor
                    credentials: 'include',
                    body: JSON.stringify({
                        email: formData.email,
                        password: formData.password
                    })
                });

                console.log('Login response status:', response.status);
                const data = await response.json();
                console.log('Login response data:', data);

                if (response.ok && data.success) {
                    localStorage.setItem('user', JSON.stringify(data.user));
                    window.location.href = '/dashboard';
                } else {
                    alert(data.message || `Login failed! Status: ${response.status}`);
                }
            } else {
                // SIGNUP LOGIC
                console.log('Signup attempt:', formData);
                const response = await fetch('http://localhost:8001/api/auth/signup/', {
                    method: 'POST',
                    headers: headers, // Düzeltilen headers kullanılıyor
                    body: JSON.stringify(formData)
                });

                console.log('Signup response status:', response.status);
                const data = await response.json();
                console.log('Signup response data:', data);

                if (response.ok && data.success) {
                    alert('Registration successful! Please login.');
                    setIsLogin(true); // Switch to login form
                    setFormData({ username: '', email: '', password: '' });
                } else {
                    alert(data.message || `Registration failed! Status: ${response.status}`);
                }
            }
        } catch (error) {
            console.error('Auth error:', error);
            alert(`Authentication error: ${error.message}`);
        }
    };

    // getCSRFToken fonksiyonu artık handleSubmit içinde kullanılıyor,
    // ancak bileşenin dışında veya formun içinde de tanımlı kalabilir.
    // Orijinal dosyadaki tanımını yoruma alabilir veya kaldırabilirsin.
    /*
    const getCSRFToken = () => {
        const cookieValue = document.cookie
            .split('; ')
            .find(row => row.startsWith('csrftoken='))
            ?.split('=')[1];
        return cookieValue || '';
    };
    */

    return (
        <div className="login-signup-container">
            {/* ... Formun geri kalanı aynı ... */}
            {isLogin ? (
                // LOGIN FORM
                <div className="form-section">
                    <h1>Giriş Yap</h1>
                    <form onSubmit={handleSubmit}>
                        {/* ... Giriş alanları ... */}
                        <div className="input-group">
                            <label>E-posta</label>
                            <i className="fas fa-envelope input-icon"></i>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>Şifre</label>
                            <i className="fas fa-lock input-icon"></i>
                            <input
                                type={showPassword ? "text" : "password"}
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Şifrenizi girin"
                                required
                            />
                            <i
                                className={`fas fa-eye${showPassword ? '-slash' : ''} password-toggle`}
                                onClick={() => setShowPassword(!showPassword)}
                            ></i>
                        </div>

                        <button type="submit" className="submit-btn">
                            Giriş Yap
                        </button>
                    </form>

                    <p className="toggle-text">
                        Hesabınız yok mu? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(false)}
                            className="toggle-btn"
                        >
                            Kayıt Ol
                        </button>
                    </p>
                </div>
            ) : (
                // SIGNUP FORM
                <div className="form-section">
                    <h1>Kayıt Ol</h1>
                    <form onSubmit={handleSubmit}>
                        <div className="input-group">
                            <label>Kullanıcı Adı</label>
                            <i className="fas fa-user input-icon"></i>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                placeholder="Kullanıcı adınızı girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>E-posta</label>
                            <i className="fas fa-envelope input-icon"></i>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>Şifre</label>
                            <i className="fas fa-lock input-icon"></i>
                            <input
                                type={showPassword ? "text" : "password"}
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Şifrenizi girin"
                                required
                            />
                            <i
                                className={`fas fa-eye${showPassword ? '-slash' : ''} password-toggle`}
                                onClick={() => setShowPassword(!showPassword)}
                            ></i>
                        </div>

                        <button type="submit" className="submit-btn">
                            Kayıt Ol
                        </button>
                    </form>

                    <p className="toggle-text">
                        Zaten hesabınız var mı? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(true)}
                            className="toggle-btn"
                        >
                            Giriş Yap
                        </button>
                    </p>
                </div>
            )}
        </div>
    );
}

export default LoginSignup;
