import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import { AuthProvider } from './AuthContext';
import './index.css';

const root = ReactDOM.createRoot(document.getElementById('root'));

const container = document.getElementById('root');
if (!container) {
    // Detayl� hata mesaj� g�ster
    const errorMessage = document.createElement('div');
    errorMessage.style.cssText = `
    padding: 20px;
    margin: 20px;
    border: 2px solid red;
    border-radius: 5px;
    background-color: #ffeeee;
    color: #cc0000;
    font-family: Arial, sans-serif;
  `;
    errorMessage.innerHTML = `
    <h2>React Uygulama Hatas�</h2>
    <p><strong>Hata:</strong> "root" ID'li element bulunamad�.</p>
    <p><strong>��z�m:</strong> public/index.html dosyan�zda a�a��daki kodu ekleyin:</p>
    <pre>&lt;div id="root"&gt;&lt;/div&gt;</pre>
    <p>Veya mevcut elementin ID'sini kontrol edin.</p>
  `;

    document.body.appendChild(errorMessage);
} else {
    // Her �ey yolundaysa React uygulamas�n� ba�lat
    const root = ReactDOM.createRoot(container);
    root.render(
        <React.StrictMode>
            <App />
        </React.StrictMode>
    );
}

root.render(
    <React.StrictMode>
        <AuthProvider>
            <App />
        </AuthProvider>
    </React.StrictMode>
);
document.addEventListener('DOMContentLoaded', function () {
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(<App />);
});
