import React, { useState } from 'react';

function LoginSignup({ onKayit }) {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    }; // BU FONKSƒ∞YON BURADA Bƒ∞TMELƒ∞ - FAZLA KOD YOK!

    // Tarayƒ±cƒ± √ßerezlerinden CSRF jetonunu √ßeken yardƒ±mcƒ± fonksiyon
    const getCSRFToken = () => {
        const cookieValue = document.cookie
            .split('; ')
            .find(row => row.startsWith('csrftoken='))
            ?.split('=')[1];
        return cookieValue || '';
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        e.stopPropagation();

        try {
            console.log('üîÑ LOGIN ƒ∞≈ûLEMƒ∞ BA≈ûLATILIYOR...');
            console.log('üìß Email:', formData.email);
            console.log('üîê Password:', '*'.repeat(formData.password.length));

            const endpoint = isLogin ? 'login' : 'signup';
            const url = `http://localhost:8001/api/auth/${endpoint}/`;

            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify(isLogin ? {
                    email: formData.email,
                    password: formData.password
                } : formData)
            });

            console.log('üì° RESPONSE STATUS:', response.status);
            console.log('üì° RESPONSE HEADERS:', response.headers);

            const contentType = response.headers.get('content-type');
            console.log('üìã CONTENT TYPE:', contentType);

            if (contentType && contentType.includes('application/json')) {
                const data = await response.json();
                console.log('üì¶ TAM RESPONSE DATA:', data);

                if (response.ok) {
                    if (isLogin) {
                        // ‚ö†Ô∏è KRƒ∞Tƒ∞K: Token'larƒ± kontrol et ve kaydet
                        console.log('üîë ACCESS TOKEN:', data.access);
                        console.log('üîÑ REFRESH TOKEN:', data.refresh);
                        console.log('üë§ USER DATA:', data.user);

                        if (!data.access) {
                            console.error('‚ùå ACCESS TOKEN ALINAMADI!');
                            alert('Backend access token d√∂nd√ºrmedi!');
                            return;
                        }

                        if (!data.refresh) {
                            console.error('‚ùå REFRESH TOKEN ALINAMADI!');
                            alert('Backend refresh token d√∂nd√ºrmedi!');
                            return;
                        }

                        // Token'larƒ± kaydet
                        localStorage.setItem('token', data.access); // 'access_token' yerine 'token'
                        localStorage.setItem('refresh', data.refresh); // 'refresh_token' yerine 'refresh'
                        localStorage.setItem('user', JSON.stringify(data.user));

                        // Kaydedildiƒüini kontrol et
                        console.log('‚úÖ LOCALSTORAGE KONTROL:');
                        console.log('   - atoken:', localStorage.getItem('token') ? '‚úÖ VAR' : '‚ùå YOK');
                        console.log('   - refresh:', localStorage.getItem('refresh') ? '‚úÖ VAR' : '‚ùå YOK');
                        console.log('   - user:', localStorage.getItem('user') ? '‚úÖ VAR' : '‚ùå YOK');

                        console.log('üéØ DASHBOARDA Y√ñNLENDƒ∞Rƒ∞Lƒ∞YOR...');
                        window.location.href = '/dashboard';

                    } else {
                        alert('Kayƒ±t ba≈üarƒ±lƒ±! Giri≈ü yapabilirsiniz.');
                        setIsLogin(true);
                        setFormData({ username: '', email: '', password: '' });
                    }
                } else {
                    console.error('‚ùå RESPONSE NOT OK:', data);
                    alert(data.detail || data.message || `ƒ∞≈ülem ba≈üarƒ±sƒ±z! Status: ${response.status}`);
                }
            } else {
                const text = await response.text();
                console.error('‚ùå BEKLENMEYEN FORMAT:', text);
                alert(`Sunucu hatasƒ±: ${response.status}. L√ºtfen konsolu kontrol edin.`);
            }

        } catch (error) {
            console.error('‚ùå AUTH ERROR:', error);
            alert(`ƒ∞≈ülem sƒ±rasƒ±nda hata olu≈ütu: ${error.message}`);
        }
    };
    return (
        <div className="login-signup-container">
            {/* ... Formun geri kalanƒ± ... */}
            {isLogin ? (
                // LOGIN FORM
                <div className="form-section">
                    <h1>Giri≈ü Yap</h1>
                    <form onSubmit={handleSubmit}>
                        {/* ... Giri≈ü alanlarƒ± ... */}
                        <div className="input-group">
                            <label>E-posta/Telefon Numarasƒ±</label>
                            <i className="fas fa-envelope input-icon"></i>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi ya da telefon numaranƒ±zƒ± girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>≈ûifre</label>
                            <i className="fas fa-lock input-icon"></i>
                            <input
                                type={showPassword ? "text" : "password"}
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="≈ûifrenizi girin"
                                required
                            />
                            <i
                                className={`fas fa-eye${showPassword ? '-slash' : ''} password-toggle`}
                                onClick={() => setShowPassword(!showPassword)}
                            ></i>
                        </div>

                        <button type="submit" className="submit-btn">
                            Giri≈ü Yap
                        </button>
                    </form>

                    <p className="toggle-text">
                        Hesabƒ±nƒ±z yok mu? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(false)}
                            className="toggle-btn"
                        >
                            Kayƒ±t Ol
                        </button>
                    </p>
                </div>
            ) : (
                // SIGNUP FORM
                <div className="form-section">
                    <h1>Kayƒ±t Ol</h1>
                    <form onSubmit={handleSubmit}>
                        <div className="input-group">
                            <label>Kullanƒ±cƒ± Adƒ±</label>
                            <i className="fas fa-user input-icon"></i>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                placeholder="Kullanƒ±cƒ± adƒ±nƒ±zƒ± girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>E-posta/Telefon Numarasƒ±</label>
                            <i className="fas fa-envelope input-icon"></i>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi ya da telefon numaranƒ±zƒ± girin"
                                required
                            />
                        </div>

                        <div className="input-group">
                            <label>≈ûifre</label>
                            <i className="fas fa-lock input-icon"></i>
                            <input
                                type={showPassword ? "text" : "password"}
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="≈ûifrenizi girin"
                                required
                            />
                            <i
                                className={`fas fa-eye${showPassword ? '-slash' : ''} password-toggle`}
                                onClick={() => setShowPassword(!showPassword)}
                            ></i>
                        </div>

                        <button type="submit" className="submit-btn">
                            Kayƒ±t Ol
                        </button>
                    </form>

                    <p className="toggle-text">
                        Zaten hesabƒ±nƒ±z var mƒ±? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(true)}
                            className="toggle-btn"
                        >
                            Giri≈ü Yap
                        </button>
                    </p>
                </div>
            )}
        </div>
    );
}

export default LoginSignup;
