import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../AuthContext";
import { useMemory } from "./MemoryContext";
import ChunkDownloader from "./ChunkDownloader";
import GroupManager from './GroupManager';
import ChatBox from "./ChatBox";
import ChatHeader from "./ChatHeader";
import UserList from "./UserList";
import TrendingView from './TrendingView';
import "./styles/Dashboard.css";
import SearchEngine from './SearchEngine';
export default function Dashboard({ notifications = [] }) {
    const { user, logout } = useAuth();
    const { fetchMemoryStats: fetchMemoryStatsFromContext } = useMemory(); // MemoryContext'ten fonksiyonu al
    const [memoryStats, setMemoryStats] = useState(null);
    const [activeTab, setActiveTab] = useState("downloader");
    const [notificationCount] = useState(notifications.length || 3);
    const [selectedThread, setSelectedThread] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [theme, setTheme] = useState('dark');

    // fetchMemoryStats fonksiyonunu tanÄ±mla
    const fetchMemoryStats = useCallback(async () => {
        try {
            console.log('ðŸ“Š Memory istatistikleri Ã§ekiliyor...');
            if (fetchMemoryStatsFromContext) {
                const stats = await fetchMemoryStatsFromContext();
                setMemoryStats(stats);
                return stats;
            } else {
                // Fallback: Direk API Ã§aÄŸrÄ±sÄ±
                const token = localStorage.getItem('token');
                const response = await fetch('http://localhost:8001/api/memory/stats/', {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                    }
                });

                if (response.ok) {
                    const stats = await response.json();
                    setMemoryStats(stats);
                    return stats;
                } else {
                    console.error('Memory stats alÄ±namadÄ±');
                    // Fallback stats
                    const fallbackStats = {
                        used_memory: 2.5,
                        total_memory: 10,
                        total_items: 25,
                        total_activities: 150,
                        file_type_count: 8
                    };
                    setMemoryStats(fallbackStats);
                    return fallbackStats;
                }
            }
        } catch (error) {
            console.error('âŒ Memory istatistikleri yÃ¼klenirken hata:', error);
            // Fallback stats
            const fallbackStats = {
                used_memory: 2.5,
                total_memory: 10,
                total_items: 25,
                total_activities: 150,
                file_type_count: 8
            };
            setMemoryStats(fallbackStats);
            return fallbackStats;
        }
    }, [fetchMemoryStatsFromContext]);

    const handleLogout = () => {
        console.log('ðŸšª Dashboard logout butonuna tÄ±klandÄ±');
        logout();
    };

    const toggleTheme = () => {
        const newTheme = theme === 'dark' ? 'light' : 'dark';
        setTheme(newTheme);
        document.documentElement.setAttribute('data-theme', newTheme);
    };

    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);

        // Memory istatistiklerini yÃ¼kle
        fetchMemoryStats();

        const fetchOnlineUsers = async () => {
            try {
                const token = localStorage.getItem('token');
                console.log('ðŸ”‘ Dashboard - Token kontrolÃ¼:', token ? 'âœ… VAR' : 'âŒ YOK');

                if (!token) {
                    console.error('âŒ Token bulunamadÄ±! Login sayfasÄ±na yÃ¶nlendiriliyor...');
                    logout();
                    return;
                }

                console.log('ðŸ”„ KullanÄ±cÄ± listesi Ã§ekiliyor...');
                const response = await fetch('http://localhost:8001/chat/users/', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                console.log('ðŸ“¡ KullanÄ±cÄ± listesi response status:', response.status);

                if (response.status === 401) {
                    console.error('âŒ Token geÃ§ersiz veya sÃ¼resi dolmuÅŸ');
                    logout();
                    return;
                }

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const users = await response.json();
                console.log(`âœ… ${users.length} kullanÄ±cÄ± yÃ¼klendi`);
                setOnlineUsers(users);

            } catch (error) {
                console.error('âŒ KullanÄ±cÄ± listesi yÃ¼klenirken hata:', error);
                setOnlineUsers([]);
            }
        };

        fetchOnlineUsers();
    }, [logout, theme, fetchMemoryStats]); // fetchMemoryStats'Ä± dependency array'e ekle

    const renderContent = () => {
        switch (activeTab) {
            case "downloader":
                return (
                    <div className="storage-memory-container">
                        <ChunkDownloader
                            apiBase="http://localhost:8001/api"
                            userEmail={user?.email}
                            theme={theme}
                            memoryStats={memoryStats}
                        />
                        <div className="memory-stats-placeholder" style={{
                            padding: '20px',
                            background: '#f5f5f5',
                            borderRadius: '8px',
                            textAlign: 'center',
                            color: '#666'
                        }}>
                            <h3>ðŸ§  HafÄ±za Ä°statistikleri</h3>
                            <p>HafÄ±za modÃ¼lÃ¼ ÅŸu anda gÃ¼ncelleniyor...</p>
                        </div>
                    </div>
                );
            case "groups":
                return <GroupManager />;
            case "chat":
                return (
                    <div className="chat-container">
                        <div className="chat-sidebar">
                            <UserList
                                users={onlineUsers}
                                currentUser={user?.username}
                                onSelectUser={(username) => {
                                    const selectedUser = onlineUsers.find(u => u.username === username);
                                    setSelectedThread({
                                        id: Date.now(),
                                        username: username,
                                        isOnline: selectedUser?.is_online || false
                                    });
                                }}
                            />
                        </div>
                        <div className="chat-main">
                            {selectedThread ? (
                                <>
                                    <ChatHeader
                                        username={selectedThread.username}
                                        isOnline={selectedThread.isOnline}
                                    />
                                    <ChatBox
                                        threadId={selectedThread.id}
                                        currentUser={user?.username}
                                    />
                                </>
                            ) : (
                                <div className="no-chat-selected">
                                    <div className="no-chat-icon">ðŸ’¬</div>
                                    <h3>Bir sohbet baÅŸlatÄ±n</h3>
                                    <p>Sohbet etmek iÃ§in soldaki listeden bir kullanÄ±cÄ± seÃ§in</p>
                                </div>
                            )}
                        </div>
                    </div>
                );
            case "ads":
                return <TrendingView />;

            case "media":
                return <SearchEngine />;

            case "profile":
                return (
                    <div className="content-placeholder">
                        <h2>Profil AyarlarÄ±</h2>
                        <p>KullanÄ±cÄ±: {user?.username}</p>
                        <p>Email: {user?.email}</p>
                    </div>
                );
            default:
                return <div className="content-placeholder">Bir iÃ§erik seÃ§in</div>;
        }
    };

    return (
        <div className="dashboard-horizontal">
            {/* Ãœst Navigasyon Bar */}
            <nav className="top-nav">
                <div className="nav-left">
                    <div className="logo">
                        <img
                            src="/qyptos-logo.png"
                            alt="qyptos-logo"
                            className="qyptos-logo-img"
                        />
                    </div>

                    <div className="nav-menu">
                        <div
                            className={`nav-item ${activeTab === "downloader" ? "active" : ""}`}
                            onClick={() => setActiveTab("downloader")}
                        >
                            <i className="fas fa-cloud"></i>
                            <span>Bulutum</span>
                        </div>

                        <div
                            className={`nav-item ${activeTab === "ads" ? "active" : ""}`}
                            onClick={() => setActiveTab("ads")}
                        >
                            <i className="fas fa-fire"></i>
                            <span>Trendler</span>
                        </div>

                        <div
                            className={`nav-item ${activeTab === "media" ? "active" : ""}`}
                            onClick={() => setActiveTab("media")}
                        >
                            <i className="fas fa-search"></i>
                            <span>Ara</span>
                        </div>

                        <div
                            className={`nav-item ${activeTab === "groups" ? "active" : ""}`}
                            onClick={() => setActiveTab("groups")}
                        >
                            <i className="fas fa-users"></i>
                            <span>Gruplar</span>
                        </div>

                        <div
                            className={`nav-item ${activeTab === "chat" ? "active" : ""}`}
                            onClick={() => setActiveTab("chat")}
                        >
                            <i className="fas fa-comments"></i>
                            <span>DM</span>
                        </div>
                    </div>
                </div>

                <div className="nav-right">
                    {/* Tema DeÄŸiÅŸtirme Butonu */}
                    <div className="theme-toggle" onClick={toggleTheme}>
                        <i className={`fas ${theme === 'dark' ? 'fa-sun' : 'fa-moon'}`}></i>
                    </div>

                    <div className="notification-bell">
                        <i className="fas fa-bell"></i>
                        {notificationCount > 0 && (
                            <span className="notification-badge">{notificationCount}</span>
                        )}
                    </div>

                    <div className="user-dropdown">
                        <div className="user-avatar">
                            <i className="fas fa-user"></i>
                        </div>
                        <span className="user-name">{user?.username || 'KullanÄ±cÄ±'}</span>
                        <i className="fas fa-chevron-down"></i>

                        <div className="dropdown-menu">
                            <div className="dropdown-item" onClick={() => setActiveTab("profile")}>
                                <i className="fas fa-user"></i>
                                <span>Profil</span>
                            </div>
                            <div className="dropdown-item">
                                <i className="fas fa-cog"></i>
                                <span>Ayarlar</span>
                            </div>
                            <div className="dropdown-divider"></div>
                            <div className="dropdown-item" onClick={handleLogout}>
                                <i className="fas fa-sign-out-alt"></i>
                                <span>Ã‡Ä±kÄ±ÅŸ Yap</span>
                            </div>
                        </div>
                    </div>
                </div>
            </nav>

            {/* Ana Ä°Ã§erik AlanÄ± */}
            <main className="main-content-horizontal">
                {renderContent()}
            </main>
        </div>
    );
}
