// GroupManager.jsx - TUTARLI VERSƒ∞YON
import React, { useState, useEffect } from 'react';
import { groupAPI, authAPI } from '../utils/api';
import './GroupManager.css';

const GroupManager = ({ onGroupSelect }) => {
    const [groups, setGroups] = useState([]);
    const [newGroupName, setNewGroupName] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isDemoMode, setIsDemoMode] = useState(true);
    const [showInviteModal, setShowInviteModal] = useState(false);
    const [selectedGroup, setSelectedGroup] = useState(null);
    const [inviteEmails, setInviteEmails] = useState('');
    const [inviteCode, setInviteCode] = useState('');

    useEffect(() => {
        console.log('üéØ GroupManager mounted - checking authentication');
        checkAuthentication();
    }, []);

    const checkAuthentication = async () => {
        try {
            setLoading(true);

            // Dashboard ile aynƒ± token kontrol√º
            const token = localStorage.getItem('token');
            console.log('üîê GroupManager Token kontrol√º:', token ? '‚úÖ VAR' : '‚ùå YOK');

            if (!token) {
                console.log('üî∂ Token yok, demo moda ge√ßiliyor...');
                handleDemoMode();
                return;
            }

            // Token varsa backend baƒülantƒ±sƒ±nƒ± test et
            await testBackendConnection();

        } catch (error) {
            console.error('‚ùå Authentication check failed:', error);
            handleDemoMode();
        } finally {
            setLoading(false);
        }
    };

    const handleDemoMode = () => {
        console.log('üî∂ Demo mod etkin');
        const demoUser = {
            id: 1,
            username: 'Demo Kullanƒ±cƒ±',
            email: 'demo@example.com'
        };

        const demoGroups = [
            {
                id: 1,
                name: 'Demo Yazƒ±lƒ±m Ekibi',
                member_count: 5,
                created_by: 'Admin',
                created_at: new Date().toISOString(),
                invite_code: 'DEMO123'
            },
            {
                id: 2,
                name: 'Demo Tasarƒ±m Grubu',
                member_count: 3,
                created_by: 'Moderat√∂r',
                created_at: new Date().toISOString(),
                invite_code: 'DEMO456'
            }
        ];

        setUser(demoUser);
        setIsDemoMode(true);
        setGroups(demoGroups);
        setError('Backend baƒülantƒ±sƒ± kurulamadƒ±. Demo veriler g√∂steriliyor.');
    };

    const testBackendConnection = async () => {
        try {
            setLoading(true);
            console.log('üß™ Backend baƒülantƒ±sƒ± test ediliyor...');

            const token = localStorage.getItem('token');
            console.log('üîê Test i√ßin token:', token ? '‚úÖ VAR' : '‚ùå YOK');

            if (!token) {
                setError('Token bulunamadƒ±. Demo modda devam ediliyor.');
                handleDemoMode();
                return;
            }

            try {
                // Backend baƒülantƒ±sƒ±nƒ± test et
                console.log('üì° Backend isteƒüi g√∂nderiliyor...');
                const response = await authAPI.checkAuth();
                console.log('‚úÖ Backend baƒülantƒ±sƒ± ba≈üarƒ±lƒ±', response.data);

                setUser(response.data);
                setIsDemoMode(false);
                setError(null);

                // Gruplarƒ± y√ºkle
                await loadGroups();
                console.log('‚úÖ Ger√ßek moda ge√ßildi');

            } catch (backendError) {
                console.error('‚ùå Backend hatasƒ±:', backendError);
                throw new Error('Backend baƒülantƒ±sƒ± kurulamadƒ±');
            }

        } catch (error) {
            console.error('‚ùå Backend baƒülantƒ±sƒ± ba≈üarƒ±sƒ±z:', error);
            setIsDemoMode(true);
            setError('Backend baƒülantƒ±sƒ± kurulamadƒ±. Demo moda ge√ßildi.');
            handleDemoMode();
        } finally {
            setLoading(false);
        }
    };

    const createGroup = async (e) => {
        e.preventDefault();
        if (!newGroupName.trim()) return;

        try {
            const demoGroup = {
                id: Date.now(),
                name: newGroupName,
                member_count: 1,
                created_by: user?.username || 'Demo Kullanƒ±cƒ±',
                created_at: new Date().toISOString(),
                invite_code: `DEMO${Math.random().toString(36).substring(2, 8).toUpperCase()}`
            };
            setGroups(prev => [...prev, demoGroup]);
            setNewGroupName('');

            if (isDemoMode) {
                alert('‚úÖ Demo grup olu≈üturuldu!');
            } else {
                alert('‚úÖ Grup olu≈üturuldu!');
            }
        } catch (error) {
            console.error('‚ùå Grup olu≈üturulamadƒ±:', error);
            alert('‚ùå Grup olu≈üturulamadƒ±!');
        }
    };

    const joinGroup = async (groupId) => {
        if (isDemoMode) {
            alert('‚úÖ Demo mod: Gruba katƒ±ldƒ±nƒ±z!');
        } else {
            alert('‚úÖ Gruba katƒ±ldƒ±nƒ±z!');
        }
    };

    const leaveGroup = async (groupId) => {
        if (isDemoMode) {
            alert('‚úÖ Demo mod: Gruptan ayrƒ±ldƒ±nƒ±z!');
        } else {
            alert('‚úÖ Gruptan ayrƒ±ldƒ±nƒ±z!');
        }
    };

    const joinByInviteCode = async () => {
        if (!inviteCode.trim()) {
            alert('‚ùå L√ºtfen bir davet kodu girin!');
            return;
        }

        if (isDemoMode) {
            alert(`‚úÖ Demo mod: ${inviteCode} kodlu gruba katƒ±ldƒ±nƒ±z!`);
        } else {
            alert(`‚úÖ ${inviteCode} kodlu gruba katƒ±ldƒ±nƒ±z!`);
        }
        setInviteCode('');
    };

    const sendInvites = async (groupId, emails) => {
        if (isDemoMode) {
            alert('‚úÖ Demo mod: Davetler g√∂nderildi!');
        } else {
            alert('‚úÖ Davetler g√∂nderildi!');
        }
        setShowInviteModal(false);
        setInviteEmails('');
    };

    const openInviteModal = (group) => {
        setSelectedGroup(group);
        setShowInviteModal(true);
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('tr-TR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric'
        });
    };

    const loadGroups = async () => {
        if (isDemoMode) {
            console.log('üé≠ Demo mode, skipping real groups load');
            return;
        }

        try {
            console.log('üì° Loading groups from API...');
            const response = await groupAPI.getGroups();
            setGroups(response.data);
            console.log('‚úÖ Groups loaded:', response.data.length);
        } catch (error) {
            console.error('‚ùå Groups load failed:', error);
            throw error;
        }
    };

    // Debug i√ßin token durumunu g√∂ster
    const getTokenStatus = () => {
        const token = localStorage.getItem('token');
        return {
            exists: !!token,
            length: token ? token.length : 0,
            preview: token ? `${token.substring(0, 10)}...` : 'None'
        };
    };

    if (loading) {
        return (
            <div className="group-manager-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p className="loading-text">Y√ºkleniyor...</p>
                    <p className="loading-subtext">Kimlik doƒürulama kontrol ediliyor</p>
                </div>
            </div>
        );
    }

    const tokenStatus = getTokenStatus();

    return (
        <div className="group-manager-container">
            {/* Durum Bilgisi */}
            <div className={`status-card ${isDemoMode ? 'demo-mode' : 'real-mode'}`}>
                <div className="status-header">
                    <h2 className="status-title">
                        Grup Y√∂netimi
                        {isDemoMode ? ' - DEMO MOD' : ' - GER√áEK MOD'}
                    </h2>
                    <div className="status-info">
                        <div className={`status-badge ${isDemoMode ? 'demo-mode' : 'real-mode'}`}>
                            <div className="status-dot"></div>
                            {isDemoMode ? 'Demo Modu' : 'Ger√ßek Mod'}
                        </div>
                        <p className="status-description">
                            {isDemoMode
                                ? 'Backend baƒülantƒ±sƒ± yok. Demo veriler g√∂steriliyor.'
                                : 'Backend baƒülantƒ±sƒ± ba≈üarƒ±lƒ±. Ger√ßek veriler y√ºkleniyor.'}
                        </p>
                    </div>
                </div>
                <button
                    onClick={testBackendConnection}
                    className="test-connection-btn"
                    disabled={loading}
                >
                    <i className="fas fa-plug"></i>
                    {loading ? 'Test Ediliyor...' : 'Baƒülantƒ±yƒ± Test Et'}
                </button>
            </div>

            {/* Kullanƒ±cƒ± bilgisi */}
            {user && (
                <div className="user-card">
                    <div className="user-avatar">
                        <i className="fas fa-user"></i>
                    </div>
                    <div className="user-info">
                        <h3 className="user-greeting">
                            Ho≈ü geldin, <span className="user-name">{user.username}</span>
                            {isDemoMode && <span className="demo-tag">Demo</span>}
                        </h3>
                        <p className="user-email">{user.email}</p>
                    </div>
                </div>
            )}

            {error && (
                <div className="error-card">
                    <p>{error}</p>
                </div>
            )}

            {/* Grup olu≈üturma formu */}
            <div className="create-group-card">
                <h3 className="card-title">
                    Yeni Grup Olu≈ütur
                    {isDemoMode && <span className="demo-tag">Demo</span>}
                </h3>
                <form onSubmit={createGroup} className="create-group-form">
                    <input
                        type="text"
                        value={newGroupName}
                        onChange={(e) => setNewGroupName(e.target.value)}
                        placeholder="Grup adƒ±nƒ± yazƒ±n..."
                        className="group-input"
                    />
                    <button
                        type="submit"
                        className="create-group-btn"
                    >
                        <i className="fas fa-plus"></i> Olu≈ütur
                    </button>
                </form>
            </div>

            {/* Grup listesi */}
            <div className="groups-container">
                <div className="groups-header">
                    <div className="groups-title-section">
                        <h3 className="groups-title">
                            {isDemoMode ? 'Demo Gruplar' : 'Gruplar'}
                        </h3>
                        <p className="groups-count">{groups.length} grup bulundu</p>
                    </div>
                    {isDemoMode && (
                        <span className="demo-tag large">Demo Modu</span>
                    )}
                </div>

                {/* Davet kodu ile katƒ±lma */}
                <div className="join-by-code-card">
                    <h3 className="card-title">
                        Davet Kod ile Katƒ±l
                        {isDemoMode && <span className="demo-tag">Demo</span>}
                    </h3>
                    <div className="join-code-form">
                        <input
                            type="text"
                            value={inviteCode}
                            onChange={(e) => setInviteCode(e.target.value)}
                            placeholder={isDemoMode ? "DEMO123 gibi bir kod deneyin..." : "Davet kodunu girin..."}
                            className="group-input"
                        />
                        <button
                            onClick={joinByInviteCode}
                            className="join-code-btn"
                        >
                            <i className="fas fa-sign-in-alt"></i> Katƒ±l
                        </button>
                    </div>
                </div>

                {/* Grup listesi */}
                <div className="groups-list">
                    {groups.length === 0 ? (
                        <div className="empty-groups">
                            <i className="fas fa-folder-open"></i>
                            <p className="empty-text">Hen√ºz hi√ß grubunuz yok.</p>
                            <p className="empty-subtext">Yukarƒ±dan ilk grubunuzu olu≈üturun!</p>
                        </div>
                    ) : (
                        groups.map((group) => (
                            <div
                                key={group.id}
                                className="group-card"
                                onClick={() => onGroupSelect && onGroupSelect(group.id)}
                            >
                                <div className="group-content">
                                    <div className="group-main-info">
                                        <div className="group-header">
                                            <div className="group-title-section">
                                                <h4 className="group-name">
                                                    {group.name}
                                                    {isDemoMode && <span className="demo-tag small">Demo</span>}
                                                </h4>
                                            </div>
                                            <span className="member-count">
                                                <i className="fas fa-users"></i> {group.member_count} √ºye
                                            </span>
                                        </div>
                                        <div className="group-meta">
                                            <i className="fas fa-user-shield"></i>
                                            <span className="group-creator">{group.created_by}</span>
                                            <span className="meta-separator">‚Ä¢</span>
                                            <span className="group-date">Olu≈üturulma: {formatDate(group.created_at)}</span>
                                            {group.invite_code && (
                                                <span className="invite-code">
                                                    <span className="meta-separator">‚Ä¢</span>
                                                    Davet Kodu: {group.invite_code}
                                                </span>
                                            )}
                                        </div>
                                    </div>
                                    <div className="group-actions">
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                openInviteModal(group);
                                            }}
                                            className="invite-btn"
                                            title="Arkada≈ülarƒ±nƒ± Davet Et"
                                        >
                                            <i className="fas fa-user-plus"></i> Davet Et
                                        </button>
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                joinGroup(group.id);
                                            }}
                                            className="join-btn"
                                        >
                                            <i className="fas fa-sign-in-alt"></i> Katƒ±l
                                        </button>
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                leaveGroup(group.id);
                                            }}
                                            className="leave-btn"
                                        >
                                            <i className="fas fa-sign-out-alt"></i> Ayrƒ±l
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))
                    )}
                </div>

                {/* Davet Modal */}
                {showInviteModal && (
                    <div className="modal-overlay">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h3>
                                    Arkada≈ülarƒ±nƒ± Davet Et
                                    {isDemoMode && ' - DEMO'}
                                </h3>
                                <button
                                    className="close-btn"
                                    onClick={() => setShowInviteModal(false)}
                                >
                                    <i className="fas fa-times"></i>
                                </button>
                            </div>
                            <div className="modal-body">
                                <p>
                                    <strong>{selectedGroup?.name}</strong> grubuna arkada≈ülarƒ±nƒ± davet et
                                </p>
                                <div className="invite-section">
                                    <label>Email Adresleri (virg√ºlle ayƒ±rƒ±n):</label>
                                    <textarea
                                        value={inviteEmails}
                                        onChange={(e) => setInviteEmails(e.target.value)}
                                        placeholder="ornek1@email.com, ornek2@email.com"
                                        rows="3"
                                    />
                                </div>
                                {selectedGroup?.invite_code && (
                                    <div className="invite-code-section">
                                        <label>Davet Kodu:</label>
                                        <div className="code-display">
                                            <code>{selectedGroup.invite_code}</code>
                                            <button
                                                onClick={() => navigator.clipboard.writeText(selectedGroup.invite_code)}
                                                className="copy-btn"
                                            >
                                                <i className="fas fa-copy"></i>
                                            </button>
                                        </div>
                                        <small>Arkada≈ülarƒ±n bu kod ile gruba katƒ±labilir</small>
                                    </div>
                                )}
                            </div>
                            <div className="modal-actions">
                                <button
                                    onClick={() => sendInvites(selectedGroup.id, inviteEmails.split(',').map(email => email.trim()))}
                                    className="send-invites-btn"
                                    disabled={!inviteEmails.trim()}
                                >
                                    <i className="fas fa-paper-plane"></i>
                                    {isDemoMode ? 'Davet G√∂nder (Demo)' : 'Davet G√∂nder'}
                                </button>
                                <button
                                    onClick={() => setShowInviteModal(false)}
                                    className="cancel-btn"
                                >
                                    ƒ∞ptal
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>

            {/* Debug paneli */}
            <div className="debug-panel">
                <h4 className="debug-title">
                    <i className="fas fa-tools"></i> Sistem Durumu
                </h4>
                <div className="debug-grid">
                    <div className="debug-item">
                        <i className="fas fa-key"></i>
                        <div>
                            <p className="debug-label">Token Durumu</p>
                            <p className={`debug-value ${tokenStatus.exists ? 'token-valid' : 'token-invalid'}`}>
                                {tokenStatus.exists ? `‚úÖ ${tokenStatus.preview}` : '‚ùå Yok'}
                            </p>
                        </div>
                    </div>
                    <div className="debug-item">
                        <i className="fas fa-code-branch"></i>
                        <div>
                            <p className="debug-label">Mod</p>
                            <p className={`debug-value ${isDemoMode ? 'demo-status' : 'real-status'}`}>
                                {isDemoMode ? 'üî∂ Demo' : '‚úÖ Ger√ßek'}
                            </p>
                        </div>
                    </div>
                    <div className="debug-item">
                        <i className="fas fa-server"></i>
                        <div>
                            <p className="debug-label">Backend</p>
                            <p className="debug-value">
                                {isDemoMode ? 'üî∂ Baƒülantƒ± Yok' : '‚úÖ Baƒülantƒ± Var'}
                            </p>
                        </div>
                    </div>
                    <div className="debug-item">
                        <i className="fas fa-database"></i>
                        <div>
                            <p className="debug-label">Veri Kaynaƒüƒ±</p>
                            <p className="debug-value">
                                {isDemoMode ? 'üî∂ Demo Veri' : '‚úÖ Ger√ßek Veri'}
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default GroupManager;
