// App.js - D�ZELT�LM�� VERS�YON
import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import { MemoryProvider } from './components/MemoryContext';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import GlobalProtection from './components/GlobalProtection';
import SecureFileViewer from './components/SecureFileViewer';
import './index.css';

function AppContent() {
    const { user, loading } = useAuth();
    const apiBase = "http://localhost:8001/api";

    if (loading) {
        return (
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100vh'
            }}>
                <p>Y�kleniyor...</p>
            </div>
        );
    }

    return (
        <Routes>
            {/* KR�T�K D�ZELTME: /share/:token rotas�n� d�zg�n �ekilde tan�mla */}
            <Route
                path="/share/:token"
                element={<SecureFileViewer />}
            />

            {/* Dashboard ve login rotalar� */}
            <Route
                path="/login"
                element={!user ? <LoginSignup apiBase={apiBase} /> : <Navigate to="/dashboard" replace />}
            />
            <Route
                path="/dashboard"
                element={user ? <Dashboard /> : <Navigate to="/login" replace />}
            />

            {/* K�k route y�nlendirmesi */}
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />

            {/* E�le�meyen di�er t�m rotalar */}
            <Route
                path="*"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
        </Routes>
    );
}

export default function App() {
    return (
        <AuthProvider>
            <GlobalProtection />
            <MemoryProvider>
                <AppContent />
            </MemoryProvider>
        </AuthProvider>
    );
}
