import React, { createContext, useState, useContext, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // Kullanıcı bilgisini localStorage'dan yükle
    const [user, setUser] = useState(() => {
        const savedUser = localStorage.getItem('user');
        return savedUser ? JSON.parse(savedUser) : null;
    });
    const [redirectTo, setRedirectTo] = useState(null);
     const [token, setToken] = useState(null);

    const login = (userData, accessToken) => {
        setUser(userData);
        setToken(accessToken);
        localStorage.setItem("user", JSON.stringify(userData));
        localStorage.setItem("token", accessToken);
    };

    const logout = async () => {
        try {
            await fetch("http://127.0.0.1:8000/auth/logout/", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ refresh: refreshToken }),
            });
        } catch (err) {
            console.error("Logout isteği başarısız:", err);
        }

        setUser(null);
        setToken(null);
        setRefreshToken(null);
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        localStorage.removeItem("refresh");
    };


    useEffect(() => {
        // localStorage’da token varsa, state’e yükle
        const savedUser = localStorage.getItem("user");
        const savedToken = localStorage.getItem("token");
        const savedRefresh = localStorage.getItem("refresh");
        if (savedUser && savedToken) {
            setUser(JSON.parse(savedUser));
            setToken(savedToken);
            setRefreshToken(savedRefresh);
        }
    }, []);

    const refreshAccessToken = async () => {
        try {
            const res = await fetch("http://127.0.0.1:8000/api/token/refresh/", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ refresh: refreshToken }),
            });
            if (res.ok) {
                const data = await res.json();
                setToken(data.access);
                localStorage.setItem("token", data.access);
            } else {
                logout();
            }
        } catch (err) {
            logout();
        }
    };

    useEffect(() => {
        if (refreshToken) {
            const interval = setInterval(refreshAccessToken, 4 * 60 * 1000);
            return () => clearInterval(interval);
        }
    }, [refreshToken]);

    // Context değerini açıkça tanımlayın
    const value = {
        user,
        login,
        logout,
        redirectTo,
        setRedirectTo
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error("useAuth must be used within an AuthProvider");
    }
    return context;
};
