import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';

function App() {
    const { user } = useAuth();
    const navigate = useNavigate(); // useNavigate hook'unu kullan�n

    useEffect(() => {
        // user durumuna g�re y�nlendirmeyi y�netin
        if (!user && window.location.pathname !== '/login') {
            navigate('/login');
        } else if (user && window.location.pathname === '/login') {
            navigate('/dashboard');
        }
    }, [user, navigate]);

    return (
        <Router>
            <div className="App">
                <Routes>
                    {/* Y�nlendirme mant��� basit�e a�a��daki gibi d�zenlenmi�tir */}
                    <Route path="/login" element={<LoginSignup />} />
                    <Route path="/dashboard" element={<Dashboard />} />

                    {/* Ana sayfa i�in y�nlendirme */}
                    <Route path="/" element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />} />
                </Routes>
            </div>
        </Router>
    );
}

// App'in do�rudan bir JSX eleman� d�nd�rmesi i�in Router i�inde sar�lmas� gerekiyor.
function AppWrapper() {
    return (
        <Router>
            <App />
        </Router>
    );
}

export default AppWrapper;
