// App.jsx (YENİ VE BİRLEŞTİRİLMİŞ SON VERSİYON)

import React from 'react';
// Sadece Routes ve Route import edin. BrowserRouter dışarıda sarmalanacak.
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from '../AuthContext';
import { MemoryProvider } from './MemoryContext';
import LoginSignup from './LoginSignup'; // Path'i kontrol edin
import Dashboard from './Dashboard'; // Path'i kontrol edin
import SecureFileViewer from './SecureFileViewer';

// App.js'den gelen mantık: Auth'a göre içeriği yöneten bileşen
function AppContent() {
    const { user, loading } = useAuth();
    const apiBase = "http://localhost:8001/api";

    // Eğer AuthContext hala kullanıcı verilerini yüklüyorsa
    if (loading) {
        // App.js'deki yükleniyor mantığı
        return (
            <div style={{ /* Stil kodunuz */ }}>
                <p>Yükleniyor...</p>
            </div>
        );
    }

    // AuthFlow mantığı yerine Rotaları kullanın:
    return (
        <Routes>
            {/* KRİTİK 1: /share/:token/ Rotası (En Kesin Eşleşme) */}
            <Route
                path="/share/:token"
                element={<SecureFileViewer />}
            />
            {/* Sondaki eğik çizgiyi (trailing slash) garantileyin */}
            <Route
                path="/share/:token/*"
                element={<SecureFileViewer />}
            />

            {/* GÜVENLİK/LOGIN ROTLARI (App.js'den Gelir) */}
            <Route
                path="/login"
                element={!user ? <LoginSignup apiBase={apiBase} /> : <Navigate to="/dashboard" replace />}
            />
            <Route
                path="/dashboard"
                element={user ? <Dashboard /> : <Navigate to="/login" replace />}
            />

            {/* KÖK ROTA: / */}
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />

            {/* Eşleşmeyen Diğer Tüm Rotalar (404 veya login/dashboard yönlendirmesi) */}
            <Route
                path="*"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
        </Routes>
    );
}

// ANA UYGULAMA KOMPONENTİ: Tüm sağlayıcıları sarmalar
function App() {
    return (
        <AuthProvider>
            {/* GlobalProtection, rotaların dışında en üstte kalmalı */}
            {/* <GlobalProtection /> buraya eklenebilir, dosya yapınızı bilmiyorum. */}
            <MemoryProvider>
                <AppContent />
            </MemoryProvider>
        </AuthProvider>
    );
}

export default App; // Sadece App bileşenini dışarı aktarın
// ReactDOM.createRoot bloğunu buradan kesinlikle SİLİN.
