import React, { createContext, useState, useContext, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // Kullanƒ±cƒ± bilgisini localStorage'dan y√ºkle
    const [user, setUser] = useState(() => {
        const savedUser = localStorage.getItem('user');
        return savedUser ? JSON.parse(savedUser) : null;
    });
    const [redirectTo, setRedirectTo] = useState(null);
    const [token, setToken] = useState(() => {
        return localStorage.getItem("token") || null;
    });
    const [refreshToken, setRefreshToken] = useState(() => {
        return localStorage.getItem("refresh") || null;
    });
    const [loading, setLoading] = useState(true); // loading state'ini buraya ta≈üƒ±

    // Loading state'ini y√∂net
    useEffect(() => {
        // ƒ∞lk y√ºklemede loading'i false yap
        setLoading(false);
    }, []);

    const login = (userData, accessToken, newRefreshToken) => {
        console.log('üîê AuthContext login √ßaƒürƒ±ldƒ±');
        setUser(userData);
        setToken(accessToken);
        setRefreshToken(newRefreshToken);
        localStorage.setItem("user", JSON.stringify(userData));
        localStorage.setItem("token", accessToken);
        localStorage.setItem("refresh", newRefreshToken);
        console.log('‚úÖ AuthContext - Kullanƒ±cƒ± bilgileri kaydedildi');
    };

    const logout = () => {
        console.log('üö™ AuthContext logout √ßaƒürƒ±ldƒ±');
        setUser(null);
        setToken(null);
        setRefreshToken(null);
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        localStorage.removeItem("refresh");
        console.log('‚úÖ AuthContext - T√ºm veriler temizlendi');

        // Login sayfasƒ±na y√∂nlendir
        window.location.href = '/login';
    };

    // Basit token kontrol√º
    const checkTokenValidity = async () => {
        const currentToken = localStorage.getItem('token');
        if (!currentToken) {
            console.log('‚ùå Token bulunamadƒ±');
            logout();
            return false;
        }
        return true;
    };

    // Context deƒüerini a√ßƒ±k√ßa tanƒ±mlayƒ±n
    const value = {
        user,
        token,
        refreshToken,
        login,
        logout,
        checkTokenValidity,
        redirectTo,
        setRedirectTo,
        loading
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error("useAuth must be used within an AuthProvider");
    }
    return context;
};
