import React, { useState } from "react";
// UI bileşenleri aynı klasörde
import { Card, CardHeader, CardContent } from "./Card";
import  Input  from "./Input";
import { Button } from "./Button";
// AuthContext bir üst klasörde (src)
import { useAuth } from "../AuthContext";

const LoginSignup = ({ apiBase }) => {
    const { login } = useAuth();
    const [mode, setMode] = useState("login");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");

    const handleSubmit = async () => {
        const endpoint = mode === "login" ? "login/" : "signup/";
        try {
            const res = await fetch(`${apiBase}/auth/${endpoint}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email, password }),
            });
            const data = await res.json();
            if (res.ok) {
                login(data.user);
                setMessage("✅ İşlem başarıyla tamamlandı!");
            } else {
                setMessage(`❌ ${data.detail || "Bir hata oluştu."}`);
            }
        } catch (err) {
            console.error(err);
            setMessage("❌ Sunucu hatası.");
        }
    };

    return (
        <Card className="max-w-md mx-auto mt-20 shadow-lg">
            <CardContent className="p-6">
                <h2 className="text-2xl font-semibold mb-4 text-center">
                    {mode === "login" ? "Giriş Yap" : "Kayıt Ol"}
                </h2>
                <div className="space-y-4">
                    <Input
                        type="email"
                        placeholder="E-posta"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Input
                        type="password"
                        placeholder="Şifre"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Button onClick={handleSubmit} className="w-full">
                        {mode === "login" ? "Giriş Yap" : "Kayıt Ol"}
                    </Button>
                    <div className="text-center text-sm text-gray-500 mt-2">
                        {mode === "login" ? (
                            <>
                                Hesabınız yok?{" "}
                                <button
                                    className="text-blue-500 underline"
                                    onClick={() => setMode("signup")}
                                >
                                    Kayıt Ol
                                </button>
                            </>
                        ) : (
                            <>
                                Hesabınız var mı?{" "}
                                <button
                                    className="text-blue-500 underline"
                                    onClick={() => setMode("login")}
                                >
                                    Giriş Yap
                                </button>
                            </>
                        )}
                    </div>
                    {message && <p className="text-center mt-2">{message}</p>}
                </div>
            </CardContent>
        </Card>
    );
};

export default LoginSignup;
