import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';

function AppContent() {
    const { user } = useAuth();
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        if (user) {
            const ws = new WebSocket('ws://localhost:3000');

            ws.onopen = () => {
                console.log('WebSocket connected successfully');
            };

            ws.onmessage = (event) => {
                try {
                    const data = JSON.parse(event.data);
                    setNotifications((prev) => [data, ...prev]);
                } catch (error) {
                    console.error('WebSocket message parsing error:', error);
                }
            };

            ws.onerror = (error) => {
                console.error('WebSocket error:', error);
            };

            ws.onclose = () => {
                console.log('WebSocket disconnected');
            };

            return () => {
                if (ws.readyState === WebSocket.OPEN) {
                    ws.close();
                }
            };
        }
    }, [user]);

    const handleKayit = async (userData) => {
        try {
            // Doğru endpoint'i kullan
            const response = await fetch('http://127.0.0.1:8001/api/auth/signup/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify({
                    username: userData.username,
                    email: userData.email,
                    password: userData.password
                })
            });

            // Response'u düzgün işle
            const responseText = await response.text();
            console.log('Raw response:', responseText);

            let result;
            try {
                result = JSON.parse(responseText);
            } catch (e) {
                console.error('JSON parse error:', e);
                return { success: false, message: 'Geçersiz sunucu yanıtı' };
            }

            console.log('Kayıt sonucu:', result);

            if (response.ok) {
                return { success: true, data: result };
            } else {
                return { success: false, message: result.error || 'Kayıt başarısız' };
            }
        } catch (error) {
            console.error('Kayıt hatası:', error);
            return { success: false, message: error.message };
        }
    };

    return (
        <Routes>
            <Route
                path="/login"
                element={!user ? <LoginSignup onKayit={handleKayit} /> : <Navigate to="/dashboard" replace />}
            />
            <Route
                path="/dashboard"
                element={user ? <Dashboard notifications={notifications} /> : <Navigate to="/login" replace />}
            />
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
            <Route path="/login.html" element={<Navigate to="/login" replace />} />
        </Routes>
    );
}

function App() {
    return (
        <Router>
            <AuthProvider>
                <AppContent />
            </AuthProvider>
        </Router>
    );
}

export default App;
