import React, { useState } from 'react';

function LoginSignup({ onKayit }) {
    const [isLogin, setIsLogin] = useState(true); // true = login form, false = signup form
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (isLogin) {
            // Login logic
            console.log('Login attempt:', formData);
            // Add your login API call here
        } else {
            // Signup logic
            const result = await onKayit(formData);
            if (result && result.success) {
                // Switch to login form after successful signup
                setIsLogin(true);
                setFormData({ username: '', email: '', password: '' }); // Clear form
            }
        }
    };

    // Only one form is displayed at a time based on isLogin state
    return (
        <div className="login-signup-container">
            {isLogin ? (
                // LOGIN FORM - only shows when isLogin is true
                <div className="form-section">
                    <h2>Giriş Yap</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label>E-posta</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi girin"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Şifre</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Şifrenizi girin"
                                required
                            />
                        </div>
                        <button type="submit" className="submit-btn">Giriş Yap</button>
                    </form>
                    <p className="toggle-text">
                        Hesabınız yok mu? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(false)}
                            className="toggle-btn"
                        >
                            Kayıt Ol
                        </button>
                    </p>
                </div>
            ) : (
                // SIGNUP FORM - only shows when isLogin is false
                <div className="form-section">
                    <h2>Kayıt Ol</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label>Kullanıcı Adı</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                placeholder="Kullanıcı adınızı girin"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>E-posta</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="E-posta adresinizi girin"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Şifre</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Şifrenizi girin"
                                required
                            />
                        </div>
                        <button type="submit" className="submit-btn">Kayıt Ol</button>
                    </form>
                    <p className="toggle-text">
                        Zaten hesabınız var mı? {' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(true)}
                            className="toggle-btn"
                        >
                            Giriş Yap
                        </button>
                    </p>
                </div>
            )}
        </div>
    );
}

export default LoginSignup;
