import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext'; // AuthProvider ve useAuth'u i�eri aktar�n
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import './index.css';

// Her response'tan �nce bu headers'� ekleyin
app.use((req, res, next) => {
    res.header('Access-Control-Allow-Origin', 'http://localhost:3000');
    res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE');
    res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization');
    res.header('Access-Control-Allow-Credentials', 'true');

    if (req.method === 'OPTIONS') {
        return res.sendStatus(200);
    }
    next();
});

const handleKayit = async (userData) => {
    try {
        const response = await fetch('/kayit', {  // ? Sadece path kullan�n
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        });

        const result = await response.json();
        console.log('Kay�t ba�ar�l�:', result);
    } catch (error) {
        console.error('Kay�t hatas�:', error);
    }
}
function AppContent() {
    const { user } = useAuth(); // AuthContext'ten kullan�c� verisini al

    return (
        <Routes>
            {/* "/login" yolu i�in bile�en */}
            <Route
                path="/login"
                element={!user ? <LoginSignup apiBase="http://localhost:3000/login.html" /> : <Navigate to="/dashboard" replace />}
            />

            {/* "/dashboard" yolu i�in bile�en */}
            <Route
                path="/dashboard"
                element={user ? <Dashboard /> : <Navigate to="/login" replace />}
            />

            {/* Ana yol "/" i�in y�nlendirme */}
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
        </Routes>
    );
}

const ws = new WebSocket('ws://localhost:3000');

ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    setNotifications((prev) => [data, ...prev]);
};

// Uygulamay� saran ana bile�en (index.js'de �a�r�lacak)
export default function App() {
    return (
        <Router>
            <AuthProvider>
                <AppContent />
            </AuthProvider>
        </Router>
    );
}
