// SecureViewer.jsx - G√úNCELLENMƒ∞≈û
import React, { useEffect, useRef, useState } from "react";

const SecureViewer = ({ file, children, user }) => {
    const [securityBreach, setSecurityBreach] = useState(false);
    const [debugInfo, setDebugInfo] = useState("Ba≈ülatƒ±lƒ±yor...");
    const [isReady, setIsReady] = useState(false);
    const containerRef = useRef(null);
    const CurrentUser = {
        username: "ahmet_yildiz",
        email: "ahmet@example.com"
    };

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsReady(true);
            setDebugInfo("‚úÖ G√ºvenlik Sistemi Hazƒ±rlandƒ± ve Kontroller Ba≈ülatƒ±ldƒ±.");
        }, 300);
        return () => clearTimeout(timer);
    }, []);

    useEffect(() => {
        if (!file?.one_time_view) {
            setDebugInfo("‚ùå one_time_view: false - Koruma GEREKSIZ");
            return;
        }

        console.log("üõ°Ô∏è SecureViewer GELISMIS KORUMA AKTIF!");
        setDebugInfo("‚úÖ Gelismis Koruma AKTIF");

        let violationCount = 0;
        const maxViolations = 2;

        // T√úM EKRAN G√ñR√úNT√úS√ú Y√ñNTEMLERƒ∞
        const detectScreenshotAttempt = (e) => {
            const screenshotPatterns = [
                // PrintScreen tu≈ülarƒ±
                e.key === 'PrintScreen' || e.key === 'Snapshot',
                e.keyCode === 44,

                // Windows + Shift + S
                (e.metaKey || e.ctrlKey) && e.shiftKey && e.key === 's',

                // Mac Screenshot
                e.metaKey && e.shiftKey && ['3', '4', '5'].includes(e.key),

                // Browser DevTools
                e.ctrlKey && e.shiftKey && ['I', 'J', 'C'].includes(e.key),

                // F12 ve diƒüer dev tools
                e.key === 'F12',
                e.keyCode === 123,

                // Right click ve inspect
                e.ctrlKey && e.key === 'u'
            ];

            if (screenshotPatterns.some(pattern => pattern)) {
                console.log(`üö® EKRAN GORUNTUSU DENEMESI: ${e.key} - Engellendi`);
                e.preventDefault();
                e.stopPropagation();
                e.stopImmediatePropagation();

                // Ekranƒ± anƒ±nda karart
                document.body.style.background = 'black';
                document.body.style.color = 'white';

                violationCount++;
                setDebugInfo(`üö® ƒ∞hlal ${violationCount}/${maxViolations}: ${e.key}`);

                // G√∂rsel uyarƒ±
                const warning = document.createElement('div');
                warning.style.cssText = `
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100vw;
                    height: 100vh;
                    background: black;
                    color: red;
                    font-size: 24px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    z-index: 99999;
                    flex-direction: column;
                `;
                warning.innerHTML = `
                    <div style="font-size: 48px; margin-bottom: 20px;">üö´</div>
                    <div>EKRAN G√ñR√úNT√úS√ú ALINAMAZ!</div>
                    <div style="font-size: 14px; margin-top: 10px;">ƒ∞hlal ${violationCount}/${maxViolations}</div>
                `;
                document.body.appendChild(warning);

                setTimeout(() => {
                    if (document.body.contains(warning)) {
                        document.body.removeChild(warning);
                    }
                }, 3000);

                if (violationCount >= maxViolations) {
                    setSecurityBreach(true);
                    setDebugInfo("üö® MAKSIMUM IHLAL - Icerik engellendi!");
                    // ƒ∞√ßeriƒüi tamamen temizle
                    if (containerRef.current) {
                        containerRef.current.innerHTML = `
                            <div style="padding: 50px; text-align: center; color: red;">
                                <h2>GUVENLIK IHLALI</h2>
                                <p>Bu icerik guvenlik nedeniyle kalici olarak engellendi.</p>
                            </div>
                        `;
                    }
                }

                return false;
            }
        };

        // GELƒ∞≈ûMƒ∞≈û EVENT YAKALAMA
        const eventOptions = { capture: true, passive: false };

        // T√ºm klavye eventleri
        document.addEventListener('keydown', detectScreenshotAttempt, eventOptions);
        document.addEventListener('keyup', detectScreenshotAttempt, eventOptions);
        window.addEventListener('keydown', detectScreenshotAttempt, eventOptions);
        window.addEventListener('keyup', detectScreenshotAttempt, eventOptions);

        // Sayfa g√∂r√ºn√ºrl√ºƒü√º
        const handleVisibilityChange = () => {
            if (document.hidden) {
                console.log("üö® Sayfa gizlendi!");
                violationCount++;
                setDebugInfo(`üö® Ihlal ${violationCount}/${maxViolations}: Sayfa gizlendi`);
            }
        };
        document.addEventListener('visibilitychange', handleVisibilityChange);

        // Saƒü tƒ±k ve men√º
        const handleContextMenu = (e) => {
            e.preventDefault();
            e.stopPropagation();
            console.log("üö® Sag tik engellendi");
        };
        document.addEventListener('contextmenu', handleContextMenu, true);

        // Se√ßimi engelle
        const handleSelectStart = (e) => {
            e.preventDefault();
            return false;
        };
        document.addEventListener('selectstart', handleSelectStart, true);

        // S√ºr√ºkle bƒ±rakƒ± engelle
        const handleDragStart = (e) => {
            e.preventDefault();
            return false;
        };
        document.addEventListener('dragstart', handleDragStart, true);

        // Copy/Cut/Paste engelle
        const handleCopy = (e) => {
            e.preventDefault();
            return false;
        };
        document.addEventListener('copy', handleCopy, true);
        document.addEventListener('cut', handleCopy, true);
        document.addEventListener('paste', handleCopy, true);

        console.log("‚úÖ Geli≈ümi≈ü event listener'lar kuruldu");

        return () => {
            document.removeEventListener('keydown', detectScreenshotAttempt, eventOptions);
            document.removeEventListener('keyup', detectScreenshotAttempt, eventOptions);
            window.removeEventListener('keydown', detectScreenshotAttempt, eventOptions);
            window.removeEventListener('keyup', detectScreenshotAttempt, eventOptions);
            document.removeEventListener('visibilitychange', handleVisibilityChange);
            document.removeEventListener('contextmenu', handleContextMenu, true);
            document.removeEventListener('selectstart', handleSelectStart, true);
            document.removeEventListener('dragstart', handleDragStart, true);
            document.removeEventListener('copy', handleCopy, true);
            document.removeEventListener('cut', handleCopy, true);
            document.removeEventListener('paste', handleCopy, true);
        };
    }, [file]);

    if (securityBreach) {
        return (
            <div style={{
                width: '100%',
                height: '400px',
                background: 'linear-gradient(45deg, #000, #222)',
                color: '#ff4444',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flexDirection: 'column',
                borderRadius: '8px',
                border: '3px solid #ff4444',
                textAlign: 'center',
                padding: '20px'
            }}>
                <div style={{ fontSize: '48px', marginBottom: '20px' }}>üö´</div>
                <h2 style={{ margin: '0 0 15px 0' }}>G√úVENLƒ∞K ƒ∞HLALƒ∞</h2>
                <p style={{ margin: '0 0 10px 0' }}>Bu i√ßerik g√ºvenlik nedeniyle engellendi.</p>
                <p style={{ margin: 0, fontSize: '14px', opacity: 0.8 }}>{debugInfo}</p>
            </div>
        );
    }

    return (

        <div
            ref={containerRef}
            className={`secure-content no-inspect no-context-menu no-drag ${securityBreach ? 'security-breach-active' : ''}`}
            style={{
                position: 'relative',
                border: file?.one_time_view ? '3px solid #ff4444' : 'none',
                borderRadius: '8px',
                overflow: 'hidden',
                background: file?.one_time_view ? '#1a1a1a' : 'transparent',
                padding: file?.one_time_view ? '10px' : '0'
            }}
        >

        <div
            ref={containerRef}
            style={{
                position: 'relative',
                border: file?.one_time_view ? '3px solid #ff4444' : 'none',
                borderRadius: '8px',
                overflow: 'hidden',
                background: file?.one_time_view ? '#1a1a1a' : 'transparent',
                padding: file?.one_time_view ? '10px' : '0',
                userSelect: 'none',
                WebkitUserSelect: 'none',
                MozUserSelect: 'none',
                msUserSelect: 'none'
            }}
            className="secure-content"
        >
            {/* DEBUG Bƒ∞LGƒ∞Sƒ∞ */}
            {process.env.NODE_ENV === 'development' && (
                <div style={{
                    position: 'absolute',
                    top: '5px',
                    left: '5px',
                    background: 'rgba(0,0,0,0.8)',
                    color: '#00ff00',
                    padding: '5px',
                    borderRadius: '3px',
                    fontSize: '10px',
                    zIndex: 10000,
                    fontFamily: 'monospace'
                }}>
                    üõ°Ô∏è {debugInfo}
                </div>
            )}

            {children}

            {/* GELƒ∞≈ûMƒ∞≈û Fƒ∞Lƒ∞GRAN Sƒ∞STEMƒ∞ */}
            {file?.one_time_view && (
                <>
                    <div style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        background: `
                            repeating-linear-gradient(
                                45deg,
                                rgba(255,0,0,0.1),
                                rgba(255,0,0,0.1) 10px,
                                rgba(0,0,255,0.1) 10px,
                                rgba(0,0,255,0.1) 20px
                            )
                        `,
                        pointerEvents: 'none',
                        zIndex: 9998
                    }} />

                    {/* Kullanƒ±cƒ± bilgisi filigranƒ± */}
                    <div style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%) rotate(-45deg)',
                        color: 'rgba(255,255,255,0.3)',
                        fontSize: '48px',
                        fontWeight: 'bold',
                        whiteSpace: 'nowrap',
                        pointerEvents: 'none',
                        zIndex: 9997,
                        textAlign: 'center'
                    }}>
                        {user?.username || 'Kullanici'} | {user?.email || ''} | {new Date().toLocaleDateString('tr-TR')}
                    </div>

                    {/* K√∂≈üe filigranlarƒ± */}
                    <div style={{
                        position: 'absolute',
                        bottom: '10px',
                        right: '10px',
                        color: 'rgba(255,255,255,0.6)',
                        fontSize: '12px',
                        background: 'rgba(0,0,0,0.8)',
                        padding: '5px 10px',
                        borderRadius: '3px',
                        pointerEvents: 'none',
                        zIndex: 9999
                    }}>
                        {file.owner?.username || 'Kullanici'} | {new Date().toLocaleString('tr-TR')}
                    </div>
                </>
            )}
            </div>
        </div>
