import React, { useState } from "react";
import { useAuth } from "../AuthContext";
import { Button } from "../ui/button";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuLabel,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Bell } from "lucide-react";

export default function Navbar({ onProfileClick }) {
    const { user, logout } = useAuth();
    const [notifications, setNotifications] = useState([
        { id: 1, text: "Yeni dosya y√ºklendi" },
        { id: 2, text: "Grubunuza yeni √ºye katƒ±ldƒ±" },
    ]);

    return (
        <div className="w-full h-14 bg-white shadow flex items-center justify-between px-6">
            <h1 className="text-xl font-bold">‚òÅÔ∏è Cloud MVP</h1>

            <div className="flex items-center gap-6">
                {/* Bildirimler */}
                <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                        <div className="relative cursor-pointer">
                            <Bell className="h-6 w-6 text-gray-700" />
                            {notifications.length > 0 && (
                                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs px-1 rounded-full">
                                    {notifications.length}
                                </span>
                            )}
                        </div>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end" className="w-56">
                        <DropdownMenuLabel>Bildirimler</DropdownMenuLabel>
                        <DropdownMenuSeparator />
                        {notifications.length === 0 ? (
                            <DropdownMenuItem className="text-gray-500">
                                Hen√ºz bildiriminiz yok
                            </DropdownMenuItem>
                        ) : (
                            notifications.map((n) => (
                                <DropdownMenuItem key={n.id}>{n.text}</DropdownMenuItem>
                            ))
                        )}
                    </DropdownMenuContent>
                </DropdownMenu>

                {/* Kullanƒ±cƒ± Men√ºs√º */}
                {user && (
                    <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                            <div className="flex items-center gap-2 cursor-pointer">
                                <Avatar className="h-8 w-8">
                                    <AvatarFallback>
                                        {user.username ? user.username[0].toUpperCase() : "U"}
                                    </AvatarFallback>
                                </Avatar>
                                <span className="font-medium">{user.username}</span>
                            </div>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end" className="w-40">
                            <DropdownMenuLabel>Hesap</DropdownMenuLabel>
                            <DropdownMenuSeparator />
                            <DropdownMenuItem onClick={onProfileClick}>üë§ Profil</DropdownMenuItem>
                            <DropdownMenuItem onClick={logout} className="text-red-600">
                                üö™ √áƒ±kƒ±≈ü
                            </DropdownMenuItem>
                        </DropdownMenuContent>
                    </DropdownMenu>
                )}
            </div>
        </div>
    );
}
