// App.js (YEN� VE B�RLE�T�R�LM�� SON VERS�YON)

import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import { MemoryProvider } from './components/MemoryContext';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import GlobalProtection from './components/GlobalProtection';
// KR�T�K: SecureFileViewer'� dahil edin
import SecureFileViewer from './components/SecureFileViewer';
import './index.css';


// Uygulama i�eri�i bile�eni (App.js'den gelen mant�k)
function AppContent() {
    const { user, loading } = useAuth();
    // const [notifications, setNotifications] = useState([]); // Bildirimler �imdilik kald�r�ld�.
    const apiBase = "http://localhost:8001/api";

    if (loading) {
        return (
            <div style={{ /* Stil kodunuz */ }}>
                <p>Y�kleniyor...</p>
            </div>
        );
    }

    return (
        <Routes>
            {/* 1. KR�T�K ROTA: /share/:token/ */}
            {/* Bu kural, /share/token veya /share/token/ gibi her iki format� da yakalar */}
            <Route
                path="/share/:token"
                element={<SecureFileViewer />}
            />
            <Route
                path="/share/:token/*"
                element={<SecureFileViewer />}
            />

            {/* 2. AUTH/DASHBOARD ROTLARI */}
            <Route
                path="/login"
                element={!user ? <LoginSignup apiBase={apiBase} /> : <Navigate to="/dashboard" replace />}
            />
            <Route
                path="/dashboard"
                element={user ? <Dashboard /> : <Navigate to="/login" replace />}
            />

            {/* K�K ROTA Y�NLEND�RMES� */}
            <Route
                path="/"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />

            {/* E�le�meyen Di�er T�m Rotalar */}
            <Route
                path="*"
                element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
            />
        </Routes>
    );
}

// Ana uygulama bile�eni
export default function App() { // export'u function'a ta��d�k
    return (
        <AuthProvider>
            {/* GlobalProtection, rotalar�n d���nda en �stte kalmal� */}
            <GlobalProtection />
            <MemoryProvider>
                <AppContent />
            </MemoryProvider>
        </AuthProvider>
    );
}
